import './InfoBoard.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faUser,
  faCartShopping,
  faCircleDollarToSlot,
  faWallet
} from "@fortawesome/free-solid-svg-icons";
import { useEffect, useState } from 'react';
import axios from '../../util/axios';

const InfoBoard = () => {
  const [users, setUsers] = useState([]);
  const [transactions, setTransactions] = useState([]);

  useEffect(() => {
    axios.get('/user')
      .then(res => {
        setUsers(res.data);
      })
      .catch(err => console.log(err));
    axios.get('/transactions')
      .then(res => {
        setTransactions(res.data);
      })
      .catch(err => console.log(err));
  })
  // console.log(transactions);
  
  return <div className="ibContainer">
    <Card className="ibCard">
      <div className="ibTitle">users</div>
      <h1 className="ibText">{users.length}</h1>
      <div className="ibIconContainer ibIconUser">
        <FontAwesomeIcon icon={faUser} />
      </div>
    </Card>
    <Card className="ibCard">
      <div className="ibTitle">orders</div>
      <h1 className="ibText">{transactions.length}</h1>
      <div className="ibIconContainer ibIconCart">
        <FontAwesomeIcon icon={faCartShopping} />
      </div>
    </Card>
    <Card className="ibCard">
      <div className="ibTitle">earnings</div>
      <h1 className="ibText">$ {transactions.map(i => i.price).reduce((cur, mov) => cur + mov, 0)}</h1>
      <div className="ibIconContainer ibIconEarn">
        <FontAwesomeIcon icon={faCircleDollarToSlot} />
      </div>
    </Card>
    <Card className="ibCard">
      <div className="ibTitle">balance</div>
      <h1 className="ibText">$ 100</h1>
      <div className="ibIconContainer ibIconBalance">
        <FontAwesomeIcon icon={faWallet} />
      </div>
    </Card>
  </div>
}

export default InfoBoard;
