import axios from "../../util/axios";
import { useContext, useState } from 'react';
import './EditHotel.css';
import { useLocation, useNavigate } from "react-router-dom";
import APIContext from "../../store/API-context";
import PageContainer from "../pageContainer/PageContainer";
import Card from "../UI/Card";
import Input from "../UI/Input";
import { validLength, validRequire } from "../../util/validators";
import { setTouched } from "../hook/lib";

const EditHotel = () => {
  const location = useLocation();
  console.log(location.state);

  // const [name, setName] = useState(location.state.name);
  // const [isValidName, setIsValidName] = useState(true);
  const [type, setType] = useState(location.state.type);
  const [isValidType, setIsValidType] = useState(true);
  const [city, setCity] = useState(location.state.city);
  const [isValidCity, setIsValidCity] = useState(true);
  const [address, setAddress] = useState(location.state.address);
  const [isValidAddress, setIsValidAddress] = useState(true);
  const [distance, setDistance] = useState(location.state.distance);
  const [isValidDistance, setIsValidDistance] = useState(true);
  const [title, setTitle] = useState(location.state.title);
  const [isValidTitle, setIsValidTitle] = useState(true);
  const [description, setDescription] = useState(location.state.description);
  const [isValidDescription, setIsValidDescription] = useState(true);
  const [price, setPrice] = useState(location.state.price);
  const [isValidPrice, setIsValidPrice] = useState(true);
  const [images, setImages] = useState(location.state.images);
  const [isValidImages, setIsValidImages] = useState(true);
  const [featured, setFeatured] = useState(location.state.featured);
  const [isValidHotelRooms, setIsValidHotelRooms] = useState(true);
  const [selectedHotelRooms, setSelectedHotelRooms] = useState([]);

  const [name, setName] = useState({
    value: faLocationPin.state.name,
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  })
  
  const [error, setError] = useState();
  const navigate = useNavigate();

  const { rooms } = useContext(APIContext);
  // console.log(rooms);

  const onChangeHanlder = (e, setInput, validators) => {
    const value = e.target.value;
    let valid = true;
    for(const validator of validators) {
      valid = valid && validator(value);
    };
    setInput(prev => ({
      ...prev,
      value: value,
      valid: valid
    }));
  };

  const onBlurHandler = (e, setInput) => {
    setInput(prev => ({
      ...prev,
      touched: true
    }));
  };

  const hotelRoomsSelectHandler = (e) => {
    const updatedOptions = [...e.target.options]
      .filter(option => option.selected)
      .map(x => x.value);
    // console.log('updatedOptions', updatedOptions);
    setSelectedHotelRooms(updatedOptions);
  }

  const editHotelHandler = (e) => {
    e.preventDefault();

    setTouched(setName);

    // if(!name) {
    //   setIsValidName(false);
    // } else {
    //   setIsValidName(true);
    // };
    if(!type) {
      setIsValidType(false);
    } else {
      setIsValidType(true);
    };
    if(!city) {
      setIsValidCity(false);
    } else {
      setIsValidCity(true);
    };
    if(!address) {
      setIsValidAddress(false);
    } else {
      setIsValidAddress(true);
    };
    if(!distance || +distance < 0) {
      setIsValidDistance(false);
    } else {
      setIsValidDistance(true);
    };
    if(!title) {
      setIsValidTitle(false);
    } else {
      setIsValidTitle(true);
    };
    if(!description) {
      setIsValidDescription(false);
    } else {
      setIsValidDescription(true);
    };
    if(!price || +price <= 0) {
      setIsValidPrice(false);
    } else {
      setIsValidPrice(true);
    };
    if(!images) {
      setIsValidImages(false);
    } else {
      setIsValidImages(true);
    };
    if(selectedHotelRooms.length === 0) {
      setIsValidHotelRooms(false);
    } else {
      setIsValidHotelRooms(true);
    };

    if(
      // isValidName === false || 
      isValidType === false ||
      isValidCity === false ||
      isValidAddress === false ||
      isValidDistance === false ||
      isValidTitle === false ||
      isValidDescription === false ||
      isValidPrice === false ||
      isValidImages === false ||
      isValidHotelRooms === false
    ) {
      return;
    }

    const editHotel = {
      id: location.state.hotelId,
      name: name,
      type: type,
      city: city.split(' ').map(i => i[0].toUpperCase() + i.substring(1)).join(' '),
      address: address,
      distance: distance,
      title: title,
      desc: description,
      cheapestPrice: price,
      photos: images.split('\n').join(','),
      featured: featured,
      rooms: selectedHotelRooms
    }

    axios
      .post('/hotel/edit-hotel', editHotel)
      .then(res => console.log(res))
      .catch(err => console.log(err))

    navigate('/hotel');
  }

  return (
    <>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={() => setError(null)}
          error={!!error}
        />
      )}
      <PageContainer>
        <Card className="ahCard">
          <div className="listTitle ahListTitle">edit hotel</div>
        </Card>
        <Card className="ahCardForm">
          <form className="formControl">
            <Input 
              control="input"
              id="name"
              type="text"
              placeholder="My Hotel"
              label="Name"
              required={true}
              onChange={e => onChangeHanlder(e, setName, name.validators)}
              onBlur={e => onBlurHandler(e, setName)}
              value={name.value}
              isValid={name.valid}
              isTouched={name.touched}
            />
            <Input 
              id="type"
              isValid={isValidType}
              label="Type"
              type="text"
              placeholder="hotel"
              value={type}
              onChange={(e) => setType(e.target.value)}
            />
            <Input 
              id="city"
              isValid={isValidCity}
              label="City"
              type="text"
              placeholder="New York"
              value={city}
              onChange={(e) => setCity(e.target.value)}
            />
            <Input 
              id="address"
              isValid={isValidAddress}
              label="Address"
              type="text"
              placeholder="eiton st. 216"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
            <Input 
              id="distance"
              isValid={isValidDistance}
              label="Distance form City Center"
              type="number"
              placeholder="500"
              value={distance}
              onChange={(e) => setDistance(e.target.value)}
            />
            <Input 
              id="title"
              isValid={isValidTitle}
              label="Title"
              type="text"
              placeholder="The best hotel"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <Input 
              id="description"
              isValid={isValidDescription}
              label="Description"
              type="text"
              placeholder="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <Input 
              id="price"
              isValid={isValidPrice}
              label="Price"
              type="number"
              placeholder="100"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
            <div className="inputFormControl">
              <div 
                className={"formLabel " + (isValidImages ? "" : "labelInvalid")}
                >Images
              </div>
              <textarea 
                name="ahImages" 
                className={"ahImages " + (isValidImages ? "" : "labelInvalid")} 
                value={images}
                onChange={(e) => setImages(e.target.value)}
                rows="3"
                cols="50"
              />
            </div>
            <div className="inputFormControl">
              <div 
                className={"formLabel "}
                >Featured
              </div>
              <select 
                name="ahFeatured" 
                id="ahFeatured"
                className={"ahFeatured "} 
                value={featured}
                onChange={(e) => setFeatured(e.target.value)}
              >
                <option value={false}>No</option>
                <option value={true}>Yes</option>
              </select>
            </div>
            <div className="inputFormControl ahHotelRooms">
              <div 
                className={"formLabel " + (isValidHotelRooms ? "" : "labelInvalid")}
                >Rooms (multiple select: ctrl + click)
              </div>
              <select 
                name="ahHotelRooms" 
                id="ahFeatured"
                className={"ahHotelRooms " + (isValidHotelRooms ? "" : "labelInvalid")} 
                value={selectedHotelRooms}
                onChange={hotelRoomsSelectHandler}
                multiple
              >
                {rooms.map(r => 
                  <option value={r._id} key={r._id}>{r.desc}</option>
                )}
              </select>
            </div>
            <button 
              className="btnSend"
              onClick={editHotelHandler}
            >Send
            </button>
          </form>
        </Card>
      </PageContainer>
    </>
  );
};

export default EditHotel;
