const initialState = {
	idUser: '',
	role: ''
};

const ReducerSession = (state = initialState, action) => {
	switch (action.type) {
		case 'ADD_SESSION':
			console.log(action.data);
			// return { idUser: action.data._id, role: action.data.role };
			// return { idUser: state.idUser, role: state.role };
			const stateLogin = [...state.idUser];
			stateLogin.idUser = action.data;
			return stateLogin;

		case 'DELETE_SESSION':
			// return { idUser: '', role: '' };
			// return { idUser: state.idUser, role: state.role };

			const stateLogout = [...state.idUser];
			stateLogout.idUser = action.data;
			return stateLogout;

		default:
			return state;
	}
};

export default ReducerSession;
