import axios from "../../util/axios";
import { useState } from 'react';
import { useAPI } from '../../store/APIContext';
import Card from '../../UI/card/Card';
import Input from '../../UI/input/Input';
import NavBar from '../navbar/NavBar';
import SideBar from '../sidebar/SideBar';
import './AddRoom.css';
import { useNavigate } from "react-router-dom";

const AddRoom = () => {
  const [title, setTitle] = useState('');
  const [isValidTitle, setIsValidTitle] = useState(true);
  const [description, setDescription] = useState('');
  const [isValidDescription, setIsValidDescription] = useState(true);
  const [price, setPrice] = useState('');
  const [isValidPrice, setIsValidPrice] = useState(true);
  const [images, setImages] = useState('');
  const [isValidImages, setIsValidImages] = useState(true);
  const [featured, setFeatured] = useState(false);
  const [isValidRoomRooms, setIsValidRoomRooms] = useState(true);
  const [selectedRoomRooms, setSelectedRoomRooms] = useState([]);
  
  const navigate = useNavigate();

  const { rooms } = useAPI();
  // console.log(rooms);

  const RoomRoomsSelectHandler = (e) => {
    const updatedOptions = [...e.target.options]
      .filter(option => option.selected)
      .map(x => x.value);
    // console.log('updatedOptions', updatedOptions);
    setSelectedRoomRooms(updatedOptions);
  }

  const addRoomHandler = (e) => {
    e.preventDefault();

    if(!name) {
      setIsValidName(false);
    } else {
      setIsValidName(true);
    };
    if(!type) {
      setIsValidType(false);
    } else {
      setIsValidType(true);
    };
    if(!city) {
      setIsValidCity(false);
    } else {
      setIsValidCity(true);
    };
    if(!address) {
      setIsValidAddress(false);
    } else {
      setIsValidAddress(true);
    };
    if(!distance || +distance < 0) {
      setIsValidDistance(false);
    } else {
      setIsValidDistance(true);
    };
    if(!title) {
      setIsValidTitle(false);
    } else {
      setIsValidTitle(true);
    };
    if(!description) {
      setIsValidDescription(false);
    } else {
      setIsValidDescription(true);
    };
    if(!price || +price <= 0) {
      setIsValidPrice(false);
    } else {
      setIsValidPrice(true);
    };
    if(!images) {
      setIsValidImages(false);
    } else {
      setIsValidImages(true);
    };
    if(selectedRoomRooms.length === 0) {
      setIsValidRoomRooms(false);
    } else {
      setIsValidRoomRooms(true);
    };

    if(
      isValidName === false || 
      isValidType === false ||
      isValidCity === false ||
      isValidAddress === false ||
      isValidDistance === false ||
      isValidTitle === false ||
      isValidDescription === false ||
      isValidPrice === false ||
      isValidImages === false ||
      isValidRoomRooms === false
    ) {
      return;
    }

    const addRoom = {
      name: name,
      type: type,
      city: city.split(' ').map(i => i[0].toUpperCase() + i.substring(1)).join(' '),
      address: address,
      distance: distance,
      title: title,
      description: description,
      price: price,
      images: images.split('\n').join(','),
      featured: featured,
      rooms: selectedRoomRooms
    }

    axios
      .post('/Room/add-Room', addRoom)
      .then(res => console.log(res))
      .catch(err => console.log(err))

    navigate('/Room');
  }

  return (
    <div className="container">
    <SideBar />
    <div>
      <NavBar />
      <div className="pageContent">
        <Card className="ahCard">
          <div className="listTitle ahListTitle">add new Room</div>
        </Card>
        <Card className="ahCardForm">
          <form className="formControl">
            <Input 
              id="title"
              isValid={isValidTitle}
              label="Title"
              type="text"
              placeholder="The best Room"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <Input 
              id="description"
              isValid={isValidDescription}
              label="Description"
              type="text"
              placeholder="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <Input 
              id="price"
              isValid={isValidPrice}
              label="Price"
              type="number"
              placeholder="100"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
            <Input 
              id="maxPeople"
              isValid={isValidMaxPeople}
              label="Max People"
              type="number"
              placeholder="100"
              value={maxPeople}
              onChange={(e) => setMaxPeople(e.target.value)}
            />
            <div className="inputFormControl">
              <div 
                className={"formLabel " + (isValidImages ? "" : "labelInvalid")}
                >Images
              </div>
              <textarea 
                name="arRooms" 
                className={"arRooms " + (isValidImages ? "" : "labelInvalid")} 
                value={a}
                onChange={(e) => setImages(e.target.value)}
                rows="3"
                cols="50"
              />
            </div>
            <div className="inputFormControl">
              <div 
                className={"formLabel "}
                >Featured
              </div>
              <select 
                name="ahFeatured" 
                id="ahFeatured"
                className={"ahFeatured "} 
                value={featured}
                onChange={(e) => setFeatured(e.target.value)}
              >
                <option value={false}>No</option>
                <option value={true}>Yes</option>
              </select>
            </div>
            <div className="inputFormControl ahRoomRooms">
              <div 
                className={"formLabel " + (isValidRoomRooms ? "" : "labelInvalid")}
                >Rooms (multiple select: ctrl + click)
              </div>
              <select 
                name="ahRoomRooms" 
                id="ahFeatured"
                className={"ahRoomRooms " + (isValidRoomRooms ? "" : "labelInvalid")} 
                value={selectedRoomRooms}
                onChange={RoomRoomsSelectHandler}
                multiple
              >
                {rooms.map(r => 
                  <option value={r._id} key={r._id}>{r.desc}</option>
                )}
              </select>
            </div>
            <button 
              className="btnSend"
              onClick={addRoomHandler}
            >Send
            </button>
          </form>
        </Card>
      </div>
    </div>
  </div>
  );
};

export default AddRoom;
