import { useEffect, useState } from 'react';
import { useAPI } from '../../store/APIContext';
import Card from '../../UI/card/Card';
import ErrorModal from '../../UI/errorModal/ErrorModal';
import './HotelList.css';
import axios from "../../util/axios";
import { useNavigate } from 'react-router-dom';

const HotelList = () => {
  const { hotels, transactions } = useAPI();
  const [error, setError] = useState();
  const [hotelId, setHotelId] = useState('');
  const [hotel, setHotel] = useState([]);

  useEffect(() => {
    setHotel(hotels);
  }, [hotels])

  const deleteHandler = (e) => {
    e.preventDefault();

    if(!transactions
        .map(i => i.hotel)
        .find(i => i.hotelId === e.target.id)
      ) {
        setError({
          title: "Delete Hotel",
          message: "Are you delete this hotel?",
          isDelete: true
        });
        setHotelId(e.target.id);
      } else {
        setError({
          title: "Cannot Delete Hotel",
          message: "This hotel is in use!",
          isDelete: false
        })
      }

  }

  const deleteHotel = () => {
    axios.post('/hotel', { hotelId: hotelId });
    setError(null);
  };

  const errorHandler = () => {
    setError(null);
  };

  const navigate = useNavigate();
  const addNewHandler = (e) => {
    e.preventDefault();
    navigate('/hotel/add-hotel');
  }

  return (
    <div className="listContainer">
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={deleteHotel}
          onCancel={errorHandler}
          isDelete={error.isDelete}
        />
      )}
      <Card className="listCard tstCard">
        <div className="listTitle tstTitle" style={{display: 'flex', justifyContent: 'space-between'}}>
          <div>hotel list</div>
          <button className="addNew" onClick={addNewHandler}>Add New</button>
        </div>
        <table>
          <thead>
            <tr>
              <th>
                <div>
                  <input type="checkbox" name="all" id="tstAll" />
                </div>
              </th>
              <th>
                <div>ID</div>
              </th>
              <th>
                <div>Name</div>
              </th>
              <th>
                <div>Type</div>
              </th>
              <th>
                <div>Title</div>
              </th>
              <th>
                <div>City</div>
              </th>
              <th>
                <div>Action</div>
              </th>
            </tr>
          </thead>
          <tbody>
            {hotel && hotel.map(i => 
              <tr key={i._id}>
                <td>
                  <input type="checkbox" />
                </td>
                <td>{i._id}</td>
                <td>{i.name}</td>
                <td>{i.type}</td>
                <td>{i.title}</td>
                <td>{i.city}</td>
                <td>
                  <button 
                    id={i._id}
                    className="delete"
                    onClick={deleteHandler}
                  >Delete</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </Card>
    </div>
  );
};

export default HotelList;
