export const validRequire = value => value.trim() !== '';

export const validLength = config => value => {
  let isValid = true;
  if(config.min) {
    isValid = isValid && value.toString().trim().length >= config.min;
  }
  if(config.max) {
    isValid = isValid && value.toString().trim().length <= config.max;
  }
  return isValid;
};

