import axios from "../../util/axios";
import { useEffect, useState } from 'react';
import { useAPI } from '../../store/APIContext';
import Card from '../../UI/card/Card';
import Input from '../../UI/input/Input';
import NavBar from '../navbar/NavBar';
import SideBar from '../sidebar/SideBar';
import './EditRoom.css';
import { useNavigate } from "react-router-dom";

const EditRoom = () => {
  const [title, setTitle] = useState('');
  const [isValidTitle, setIsValidTitle] = useState(true);
  const [description, setDescription] = useState('');
  const [isValidDescription, setIsValidDescription] = useState(true);
  const [price, setPrice] = useState('');
  const [isValidPrice, setIsValidPrice] = useState(true);
  const [maxPeople, setMaxPeople] = useState('');
  const [isValidMaxPeople, setIsValidMaxPeople] = useState(true);
  const [name, setName] = useState('');
  const [isValidName, setIsValidName] = useState(true);
  const [hotel, setHotel] = useState('Choose a hotel');
  const [isValidHotel, setIsValidHotel] = useState(true);
  
  const navigate = useNavigate();
  
  const { rooms, hotels } = useAPI();
  // console.log(rooms);

  const editRoomHandler = (e) => {
    e.preventDefault();

    if(!title) {
      setIsValidTitle(false);
    } else {
      setIsValidTitle(true);
    };
    if(!description) {
      setIsValidDescription(false);
    } else {
      setIsValidDescription(true);
    };
    if(!price || +price <= 0) {
      setIsValidPrice(false);
    } else {
      setIsValidPrice(true);
    };
    if(!maxPeople || +maxPeople <= 0) {
      setIsValidMaxPeople(false);
    } else {
      setIsValidMaxPeople(true);
    };
    if(
      !name 
      // || 
      // name.split(',').map(i => i*1).find(i => isNaN(i)) !== undefined ||
      // name.split(',').filter((v, i, arr) => arr.indexOf(v) === i).length !== name.length ||
      // name.split(',').filter(v => v !== "").length !== name.length
    ) {
      setIsValidName(false);
    } else {
      setIsValidName(true);
    };
    if(hotel === 'Choose a hotel') {
      setIsValidHotel(false);
    } else {
      setIsValidHotel(true);
    }

    if(
      isValidTitle === false ||
      isValidDescription === false ||
      isValidPrice === false ||
      isValidMaxPeople === false ||
      isValidName === false ||
      isValidHotel === false
    ) {
      return;
    }

    const editRoom = {
      title: title.split(' ').map(i => i[0].toUpperCase() + i.substring(1)).join(' '),
      description: (description.split(' ').join(' ').charAt(0).toUpperCase() + description.split(' ').join(' ').slice(1)),
      price: price,
      maxPeople: maxPeople,
      name: name.split(',').map(Number),
      hotel: hotel
    }

    axios
      .post('/room/edit-room', editRoom)
      .then(res => console.log(res))
      .catch(err => console.log(err))

    console.log(name.split(',').map(Number));

    navigate('/room');

    console.log(isValidName);
    console.log("arRooms " + (isValidName ? "" : "labelInvalid"));
    // console.log(!name);
    // console.log(name.split(',').map(i => i*1).find(i => isNaN(i)) !== undefined);
    // console.log(name.split(',').filter((v, i, arr) => arr.indexOf(v) === i).length !== name.split(',').length);
    // console.log(name.split(',').filter(v => v !== "").length !== name.split(',').length);
    // console.log(name.split(',').map(i => i*1).find(i => isNaN(i)) !== undefined || name.split(',').filter((v, i, arr) => arr.indexOf(v) === i).length !== name.split(',').length || name.split(',').filter(v => v !== "").length !== name.split(',').length);
  }

  return (
    <div className="container">
    <SideBar />
    <div>
      <NavBar />
      <div className="pageContent">
        <Card className="ahCard">
          <div className="listTitle ahListTitle">edit Room</div>
        </Card>
        <Card className="ahCardForm">
          <form className="formControl">
            <Input 
              id="title"
              isValid={isValidTitle}
              label="Title"
              type="text"
              placeholder="2 bed room"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <Input 
              id="description"
              isValid={isValidDescription}
              label="Description"
              type="text"
              placeholder="King size bed, 1 bathroom"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <Input 
              id="price"
              isValid={isValidPrice}
              label="Price"
              type="number"
              placeholder="100"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
            <Input 
              id="maxPeople"
              isValid={isValidMaxPeople}
              label="Max People"
              type="number"
              placeholder="2"
              value={maxPeople}
              onChange={(e) => setMaxPeople(e.target.value)}
            />
            <div className="inputFormControl">
              <div 
                className={"formLabel " + (isValidName ? "" : "labelInvalid")}
                >Rooms
              </div>
              <textarea 
                name="arRooms" 
                className={"arRooms " + (isValidName ? "" : "labelInvalid")} 
                value={name}
                onChange={(e) => setName(e.target.value)}
                rows="3"
                cols="50"
                // placeholder="Give a comma between room and numbers."
              />
            </div>
            <div className="inputFormControl">
              <div 
                className={"formLabel " + (isValidHotel ? "" : "labelInvalid")}
                >Choose a hotel
              </div>
              <select 
                name="arHotel" 
                id="arHotel"
                className={"arHotel " + (isValidHotel ? "" : "labelInvalid")} 
                value={hotel}
                onChange={(e) => setHotel(e.target.value)}
              >
                <option value="Choose a hotel">Choose a hotel</option>
                {hotels.map(i =>
                  <option 
                    key={i._id}
                    value={i._id}
                  >{i.name}</option>
                )}
              </select>
            </div>
            <button 
              className="btnSend"
              onClick={editRoomHandler}
            >Send
            </button>
          </form>
        </Card>
      </div>
    </div>
  </div>
  );
};

export default EditRoom;
