import queryString from 'query-string';
import { useEffect, useState } from 'react';
import ChatRoomsAPI from '../../API/ChatRoomsAPI';
import { onBlurHandler, onChangeHanlder, valid } from '../../util/validators';
import Input from '../UI/Input';
import './ChatList.css';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faPaperPlane
} from "@fortawesome/free-solid-svg-icons";

const ChatList = () => {
  const [chatSearch, setChatSearch] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [valid({ type: true })]
  });
  const [textMessage, setTextMessage] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [valid({ type: true })]
  });
  const [chatList, setChatList] = useState([]);
  const [message, setMessage] = useState([]);
  const [roomId, setRoomId] = useState('');
  const [load, setLoad] = useState(false);
  const cookieId = document.cookie.split('=')[1];

  console.log(chatSearch.value);

  useEffect(() => {
    const searchMessage = async() => {
      const params = {
        cookieId: cookieId,
        chatSearch: chatSearch.value
      }
      const query = '?' + queryString.stringify(params);
      const response = await ChatRoomsAPI.searchMessage(query);
      setChatList(response);
    }
    searchMessage();
  }, [chatSearch])
  
  useEffect(() => {
    const getAllRoom = async() => {
      const params = {
        cookieId: cookieId
      }
      const query = '?' + queryString.stringify(params);
      const response = await ChatRoomsAPI.getAllRoom(query);
      setChatList(response);
      if(response) {
        setLoad(false);
      }
    }
    getAllRoom();
  }, [])

  console.log(chatSearch.value);
  console.log(chatList);
  console.log(load);
  
  const onChat = (e) => {
    e.preventDefault();
    setRoomId(e.target.id);
    console.log(e.target.id);
  }

  const getMessageByRoomId = async () => {
		const response = await ChatRoomsAPI.getMessageByRoomId(roomId);
		console.log(response);
		setMessage(response.content);
    if(response) {
      setLoad(false);
    }
	};
  
	useEffect(() => {
    setLoad(true);
    if(roomId.trim() !== '') {
      getMessageByRoomId();
    }
    setTimeout(() => {
      setLoad(false)
    }, 2000)
	}, [roomId])

  return (
    <div>
      <h1>Chat</h1>
      <em>Apps / Chat</em>
      {load && (
				<div className='wrapper_loader'>
					<div className='loader'></div>
				</div>
			)}
      {!load && 
      <div className='bg-white container'>
        <div className="chatInfo flex-column">
          <div className="chatSearch">
            <Input 
              control='input'
              type='search'
              id='chatSearch'
              placeholder='Search Contact'
              required={true}
              onChange={e => onChangeHanlder(e, setChatSearch, chatSearch.validators)}
              onBlur={e => onBlurHandler(e, setChatSearch, chatSearch.validators)}
              value={chatSearch.value}
              isValid={chatSearch.valid}
              isTouched={chatSearch.touched}
              className='chatListItem'
            />
            {chatList && chatList.map(value => 
              <div 
                key={value._id} 
                className='flex chatListItem'
              >
                <img
                  className='avatar'
                  src='https://img.icons8.com/color/36/000000/administrator-male.png'
                  alt='...'
                />
                <div 
                  id={value._id.toString()} 
                  onClick={onChat}
                >{value._id}</div>
              </div>
            )}
          </div>
        </div>
        <div className="chatBox">
          <div className="chatMedia">
            {message && message.map(value =>
              value.is_admin ? (
                <div
                  className='media media-chat'
                  key={value._id}>
                  <div className='media-body'>
                    <p>You: {value.message}</p>
                  </div>
                </div>
              ) : (
                <div
                  className='media media-chat media-chat-reverse'
                  key={value._id}>
                  {' '}
                  <img
                    className='avatar'
                    src='https://img.icons8.com/color/36/000000/administrator-male.png'
                    alt='...'
                  />
                  <div className='media-body' key={value.id}>
                    <p>Client: {value.message}</p>
                  </div>
                </div>
              )
            )}
          </div>
          <div className="chatField flex">
            <Input 
              control='input'
              type='text'
              id='textMessage'
              placeholder='Type and enter'
              required={true}
              onChange={e => onChangeHanlder(e, setTextMessage, textMessage.validators)}
              onBlur={e => onBlurHandler(e, setTextMessage, textMessage.validators)}
              value={textMessage.value}
              isValid={textMessage.valid}
              isTouched={textMessage.touched}
              className='chatListItem'
            />
            <div className='chatFieldIcon'>
              <FontAwesomeIcon icon={faPaperPlane} className="sbIcon" />
            </div>
          </div>
        </div>   
      </div>
      }
    </div>
  )
};

export default ChatList;
