import axios from "../../util/axios";
import { useContext, useState } from "react"
import Navbar from "../navbar/Navbar"
import './auth.css'
import { useNavigate } from "react-router-dom";
import APIContext from "../../store/API-context";

const SignUp = (pros) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [errorUsername, setErrorUsername] = useState('');
  const [errorPassword, setErrorPassword] = useState('');

  const [isErrorUsername, setIsErrorUsername] = useState(false);
  const [isErrorPassword, setIsErrorPassword] = useState(false);

  // const { users } = useAPI();
  const { users } = useContext(APIContext);
  
  const navgigate = useNavigate('/');

  const onChangeUsername = (e) => {
    setUsername(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const signUpHandler = (e) => {
    e.preventDefault();

    if(username.trim() === '') {
      setIsErrorUsername(true);
      setErrorUsername('username is empty');
    };
    
    if(users.find(i => i.username === username)) {
      setIsErrorUsername(true);
      setErrorUsername({username} + ' is exist');
    };

    if(password.trim() === '') {
      setIsErrorPassword(true);
      setErrorPassword('password is empty');
    };

    if(isErrorUsername || isErrorPassword) {
      return;
    }
    
    setIsErrorUsername(false);
    setIsErrorPassword(false);

    if(!isErrorUsername && !isErrorPassword) {
      axios.post('/user', {
        username,
        password
      })
      .then(res => console.log(res))
      .catch(err => console.log(err))
    }

    navgigate('/');
  };

  return <>
    <Navbar/>
    <main className="centered">
      <h1>Sign Up</h1>
      <form className="form-group">
        <input 
          type="text" 
          placeholder="username" 
          className="form-control" 
          value={username}
          onChange={onChangeUsername}
        />
        {isErrorUsername && <span style={{ color: "red", marginBottom: "1rem" }}>{errorUsername}</span>}
        <input 
          type="password" 
          placeholder="password" 
          className="form-control" 
          value={password}
          onChange={onChangePassword}
        />
        {isErrorPassword && <span style={{ color: "red", marginBottom: "1rem" }}>{errorPassword}</span>}
        <button 
          type="submit" 
          className="btn"
          onClick={signUpHandler}
        >
          Create Account
        </button>
      </form>
    </main>
  </>
}

export default SignUp;
