import './RoomList.css';
import { useEffect, useState } from 'react';
import { useAPI } from '../../store/APIContext';
import Card from '../../UI/card/Card';
import ErrorModal from '../../UI/errorModal/ErrorModal';
import axios from "../../util/axios";
import { useNavigate } from 'react-router-dom';

const RoomList = () => {
  const { transactions } = useAPI();
  const [error, setError] = useState();
  const [hotelId, setHotelId] = useState('');
  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    async function fetchData() {
      const request = axios.get('/room');
      setRooms(request.data);
    };
    fetchData();
  }, []);

  const deleteHandler = (e) => {
    e.preventDefault();

    if(!transactions
        .map(i => i.hotel)
        .find(i => i.hotelId === e.target.id)
      ) {
        setError({
          title: "Delete Hotel",
          message: "Are you delete this hotel?",
          isDelete: true
        });
        setHotelId(e.target.id);
      } else {
        setError({
          title: "Cannot Delete Hotel",
          message: "This hotel is in use!",
          isDelete: false
        })
      }

  }

  const deleteHotel = () => {
    axios
      .post('/hotel', { hotelId: hotelId })
      .then(res => setHotels(prev => prev.filter(i => i._id !== hotelId)))
    setError(null);
  };


  const errorHandler = () => {
    setError(null);
  };

  const navigate = useNavigate();
  
  const addNewHandler = (e) => {
    e.preventDefault();
    navigate('/hotel/add-hotel');
  }

  return (
    <div className="listContainer">
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={deleteHotel}
          onCancel={errorHandler}
          isDelete={error.isDelete}
        />
      )}
      <Card className="listCard tstCard">
        <div className="listTitle tstTitle" style={{display: 'flex', justifyContent: 'space-between'}}>
          <div>hotel list</div>
          <button className="addNew" onClick={addNewHandler}>Add New</button>
        </div>
        <table>
          <thead>
            <tr>
              <th>
                <div>
                  <input type="checkbox" name="all" id="tstAll" />
                </div>
              </th>
              <th>
                <div>ID</div>
              </th>
              <th>
                <div>Title</div>
              </th>
              <th>
                <div>Description</div>
              </th>
              <th>
                <div>Price</div>
              </th>
              <th>
                <div>Max People</div>
              </th>
              <th>
                <div>Action</div>
              </th>
            </tr>
          </thead>
          <tbody>
            {rooms && rooms.map(i => 
              <tr key={i._id}>
                <td>
                  <input type="checkbox" />
                </td>
                <td>{i._id}</td>
                <td>{i.title}</td>
                <td>{i.desc}</td>
                <td>{i.price}</td>
                <td>{i.maxPeople}</td>
                <td>
                  <button 
                    id={i._id}
                    className="delete"
                    onClick={deleteHandler}
                  >Delete</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </Card>
    </div>
  );
};

export default RoomList;
