import { useContext, useState } from "react";
import './auth.css'
import { useDispatch } from "react-redux";
import { authActions } from "../hook/auth";
import { currentUser, saveToStorage, setTouched } from "../hook/lib";
import { useLocation, useNavigate } from "react-router-dom";
import Input from "../UI/Input";
import ErrorModal from "../UI/ErrorModal";
import { onBlurHandler, onChangeHanlder, validEmail, validLength, validRequire } from "../../util/validators";
import PageContainer from "../pageContainer/PageContainer";
import queryString from "query-string";
import UserAPI from "../../API/UserAPI";

const Login = (pros) => {
  const [email, setEmail] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validEmail, validRequire]
  });
  const [password, setPassword] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });

  const [error, setError] = useState();
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const loginHandler = (e) => {
    e.preventDefault();

    setTouched(setEmail);
    setTouched(setPassword);

    console.log(email.value);

    if(email.valid) {
      const params = {
        email: email.value,
        password: password.value
      }
      const query = '?' + queryString.stringify(params);
      UserAPI.postLogin(query)
        .then(res => {
          if(res) {
            console.log(res);
            dispatch(authActions.login());
            // while(currentUser.length > 0) {
            //   currentUser.pop();
            // };
            // currentUser.push(res);
            saveToStorage('id_user', res._id);
            setCookie('userId', res._id, +1);
            navigate('/home');
          }
        })
        .catch(err => {
          console.log(err);
          setError({
            title: 'Validation failed!',
            message: err.response.data.message
          })
        });
    };

    if(!email.valid || !password.valid) {
      // console.log('no');
      return setError({
        title: 'Validation failed!',
        message: 'Invalid email or password!'
      })
    }
  };

  return (
    <>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={() => setError(null)}
          error={!!error}
        />
      )}
      <PageContainer>
        <main className="centered">
          <h1>Login</h1>
          <form className="form-group">
            <Input
              control="input"
              id="email"
              type="text" 
              placeholder="email" 
              required={true}
              onChange={e => onChangeHanlder(e, setEmail, email.validators)}
              onBlur={e => onBlurHandler(e, setEmail, email.validators)}
              value={email.value}
              isValid={email.valid}
              isTouched={email.touched}
            />
            <Input 
              control="input"
              id="password"
              type="password" 
              placeholder="password"
              onChange={e => onChangeHanlder(e, setPassword, password.validators)}
              onBlur={e => onBlurHandler(e, setPassword, password.validators)}
              required={true}
              value={password.value}
              isValid={password.valid}
              isTouched={password.touched}
            />
            <button 
              type="submit" 
              className="btn"
              onClick={loginHandler}
            >Login</button>
          </form>
        </main>
      </PageContainer>
    </>
  );
};

export default Login;
