import { useState } from 'react';
import { useAPI } from '../../store/APIContext';
import Card from '../../UI/card/Card';
import './Transaction.css';

const Transaction = () => {
  const { transactions } = useAPI();
  console.log(transactions);
  const { users } = useAPI();
  console.log(users);
  const [page, setPage] = useState(1);

  const tstLimit = 8;
  const totalPages = Math.ceil(transactions.length / tstLimit);
  const pageResults = page !== totalPages ? transactions.slice((page - 1), tstLimit * page) : transactions.slice((totalPages - 1)*tstLimit, tstLimit * page);

  // if(pageResults) {
  //   if(+page === +totalPages) {
  //     document.querySelector(".pageNext").setAttribute("disabled", "");
  //   } else {
  //     document.querySelector(".pageNext").removeAttribute("disabled");
  //   }

  //   if(+page === 1) {
  //     document.querySelector(".pagePrev").setAttribute("disabled", "");
  //   } else {
  //     document.querySelector(".pagePrev").removeAttribute("disabled");
  //   }
  // }

  return <div className="listContainer">
    <Card className="listCard tstCard">
      <div className="listTitle tstTitle">latest transactions</div>
      <table>
        <thead>
          <tr>
            <th>
              <div>
                <input type="checkbox" name="all" id="tstAll" />
              </div>
            </th>
            <th>
              <div>ID</div>
            </th>
            <th>
              <div>User</div>
            </th>
            <th>
              <div>Hotel</div>
            </th>
            <th>
              <div>Room</div>
            </th>
            <th>
              <div>Date</div>
            </th>
            <th>
              <div>Price</div>
            </th>
            <th>
              <div>Payment Method</div>
            </th>
            <th>
              <div>Status</div>
            </th>
          </tr>
        </thead>
        <tbody>
          {pageResults && pageResults.map(i => 
            <tr key={i._id}>
              <td>
                <input type="checkbox" />
              </td>
              <td>{i._id}</td>
              <td>{i.user.username}</td>
              <td>{i.hotel.name}</td>
              <td>{i.room.map(r => r.roomNumber).join(',')}</td>
              <td>{i.dateStart} - {i.dateEnd}</td>
              <td>${i.price}</td>
              <td>{i.payment}</td>
              <td>
                <div className={'tstStatus ' 
                  + (i.status === 'Booked' ? 'stsBooked' : '')
                  + (i.status === 'Checkin' ? 'stsCheckin' : '')
                  + (i.status === 'Checkout' ? 'stsCheckout' : '')
                }>
                  {i.status}
                </div>
              </td>
            </tr>
          )}
          <tr>
            {/* <td></td>
            <td></td>
            <td></td>
            <td></td>
            <td></td>
            <td></td>
            <td></td>
            <td></td> */}
            
          </tr>
        </tbody>
      </table>
      <div className="page">
        <div className="pageNum">
          {page} - {tstLimit} of {totalPages} 
        
          <button 
            className="pagePrev" 
            onClick={() => setPage((prev) => prev - 1)}
            disabled={page === 1}
          >&lt;
          </button>
          <button 
            className="pageNext" 
            onClick={() => setPage((prev) => prev + 1)}
            disabled={page === totalPages}
          >&gt;
          </button>
        </div>
      </div>
    </Card>
  </div>
}

export default Transaction;
