import axios from "../../util/axios";
import { useContext, useState } from 'react';
import './EditHotel.css';
import { useLocation, useNavigate } from "react-router-dom";
import APIContext from "../../store/API-context";
import PageContainer from "../pageContainer/PageContainer";
import Card from "../UI/Card";
import Input from "../UI/Input";
import { onBlurHandler, onChangeHanlder, validate, validLength, validNumber, validRequire } from "../../util/validators";
import { setTouched } from "../hook/lib";
import ErrorModal from "../UI/ErrorModal";

const EditHotel = () => {
  const location = useLocation();
  console.log(location.state);

  const [isValidHotelRooms, setIsValidHotelRooms] = useState(true);
  const [selectedHotelRooms, setSelectedHotelRooms] = useState(location.state.rooms);

  const [name, setName] = useState({
    value: location.state.name,
    valid: location.state ? true : false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [type, setType] = useState({
    value: location.state.type,
    valid: location.state ? true : false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [city, setCity] = useState({
    value: location.state.city,
    valid: location.state ? true : false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [address, setAddress] = useState({
    value: location.state.address,
    valid: location.state ? true : false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [distance, setDistance] = useState({
    value: location.state.distance,
    valid: location.state ? true : false,
    touched: false,
    validators: [validNumber({ min: 0 }), validRequire]
  });
  const [title, setTitle] = useState({
    value: location.state.title,
    valid: location.state ? true : false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [description, setDescription] = useState({
    value: location.state.description,
    valid: location.state ? true : false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [price, setPrice] = useState({
    value: location.state.price,
    valid: location.state ? true : false,
    touched: false,
    validators: [validNumber({ min: 0 }), validRequire]
  });
  const [images, setImages] = useState({
    value: location.state.images,
    valid: location.state ? true : false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [featured, setFeatured] = useState(location.state.featured);
  
  const [error, setError] = useState();
  const navigate = useNavigate();

  const [rooms, setRooms] = useState([]);
  // console.log(rooms);

  const hotelRoomsSelectHandler = (e) => {
    const updatedOptions = [...e.target.options]
      .filter(option => option.selected)
      .map(x => x.value);
    // console.log('updatedOptions', updatedOptions);
    setSelectedHotelRooms(updatedOptions);
  }

  const editHotelHandler = (e) => {
    e.preventDefault();

    setTouched(setName);
    setTouched(setType);
    setTouched(setCity);
    setTouched(setAddress);
    setTouched(setDistance);
    setTouched(setTitle);
    setTouched(setDescription);
    setTouched(setPrice);
    setTouched(setImages);

    if(selectedHotelRooms.length === 0) {
      setIsValidHotelRooms(false);
    } else {
      setIsValidHotelRooms(true);
    };

    if(
      isValidHotelRooms === false ||
      !name.valid ||
      !type.valid ||
      !city.valid ||
      !address.valid ||
      !distance.valid ||
      !title.valid ||
      !description.valid ||
      !price.valid ||
      !images.valid
    ) {
      return;
    }

    const editHotel = {
      id: location.state.hotelId,
      name: name.value,
      type: type.value,
      city: city.value.split(' ').map(i => i[0].toUpperCase() + i.substring(1)).join(' '),
      address: address.value,
      distance: distance.value,
      title: title.value.split(' ').join(' ').charAt(0).toUpperCase() + title.split(' ').join(' ').slice(1),
      description: description.value.split(' ').join(' ').charAt(0).toUpperCase() + description.split(' ').join(' ').slice(1),
      price: price.value,
      // images: images.value.split('\n').join(','),
      images: images.value,
      featured: featured,
      rooms: selectedHotelRooms
    }

    axios
      .post('/hotel/edit-hotel', editHotel)
      .then(res => console.log(res))
      .catch(err => console.log(err))

    navigate('/hotel');
  }

  return (
    <>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={() => setError(null)}
          error={!!error}
        />
      )}
      <PageContainer>
        <Card className="ahCard">
          <div className="listTitle ahListTitle">edit hotel</div>
        </Card>
        <Card className="ahCardForm">
          <form className="formControl">
            <Input 
              control="input"
              id="name"
              type="text"
              placeholder="My Hotel"
              label="Name"
              required={true}
              onChange={e => onChangeHanlder(e, setName, name.validators)}
              onBlur={e => onBlurHandler(e, setName, name.validators)}
              value={name.value}
              isValid={name.valid}
              isTouched={name.touched}
            />
            <Input 
              control="input"
              id="type"
              type="text"
              placeholder="hotel"
              label="Type"
              required={true}
              onChange={e => onChangeHanlder(e, setType, type.validators)}
              onBlur={e => onBlurHandler(e, setType, type.validators)}
              value={type.value}
              isValid={type.valid}
              isTouched={type.touched}
            />
            <Input 
              control="input"
              id="city"
              type="text"
              placeholder="New York"
              label="City"
              required={true}
              onChange={e => onChangeHanlder(e, setCity, city.validators)}
              onBlur={e => onBlurHandler(e, setCity, city.validators)}
              value={city.value}
              isValid={city.valid}
              isTouched={city.touched}
            />
            <Input 
              control="input"
              id="address"
              type="text"
              placeholder="eiton st. 216"
              label="Address"
              required={true}
              onChange={e => onChangeHanlder(e, setAddress, address.validators)}
              onBlur={e => onBlurHandler(e, setAddress, address.validators)}
              value={address.value}
              isValid={address.valid}
              isTouched={address.touched}
            />
            <Input 
              control="input"
              id="distance"
              type="number"
              placeholder="500"
              label="Distance form City Center"
              required={true}
              onChange={e => onChangeHanlder(e, setDistance, distance.validators)}
              onBlur={e => onBlurHandler(e, setDistance, distance.validators)}
              value={distance.value}
              isValid={distance.valid}
              isTouched={distance.touched}
            />
            <Input 
              control="input"
              id="title"
              type="text"
              placeholder="The best hotel"
              label="Title"
              required={true}
              onChange={e => onChangeHanlder(e, setTitle, title.validators)}
              onBlur={e => onBlurHandler(e, setTitle, title.validators)}
              value={title.value}
              isValid={title.valid}
              isTouched={title.touched}
            />
            <Input 
              control="input"
              id="description"
              type="text"
              placeholder="description"
              label="Description"
              required={true}
              onChange={e => onChangeHanlder(e, setDescription, description.validators)}
              onBlur={e => onBlurHandler(e, setDescription, description.validators)}
              value={description.value}
              isValid={description.valid}
              isTouched={description.touched}
            />
            <Input 
              control="input"
              id="price"
              type="number"
              placeholder="100"
              label="Price"
              required={true}
              onChange={e => onChangeHanlder(e, setPrice, price.validators)}
              onBlur={e => onBlurHandler(e, setPrice, price.validators)}
              value={price.value}
              isValid={price.valid}
              isTouched={price.touched}
            />
            <Input 
              control="textarea"
              id="images"
              type="textarea"
              placeholder=""
              label="Images"
              required={true}
              onChange={e => onChangeHanlder(e, setImages, images.validators)}
              onBlur={e => onBlurHandler(e, setImages, images.validators)}
              value={images.value}
              isValid={images.valid}
              isTouched={images.touched}
              rows="3"
              cols="50"
            />
            <div className="inputFormControl">
              <div 
                className={"formLabel "}
                >Featured
              </div>
              <select 
                name="ahFeatured" 
                id="ahFeatured"
                className={"ahFeatured "} 
                value={featured}
                onChange={(e) => setFeatured(e.target.value)}
              >
                <option value={false}>No</option>
                <option value={true}>Yes</option>
              </select>
            </div>
            <div className="inputFormControl ahHotelRooms">
              <div 
                className={"formLabel " + (isValidHotelRooms ? "" : "labelInvalid")}
                >Rooms (multiple select: ctrl + click)
              </div>
              <select 
                name="ahHotelRooms" 
                id="ahFeatured"
                className={"ahHotelRooms " + (isValidHotelRooms ? "" : "labelInvalid")} 
                value={selectedHotelRooms}
                onChange={hotelRoomsSelectHandler}
                multiple
              >
                {rooms.map(r => 
                  <option value={r._id} key={r._id}>{r.desc}</option>
                )}
              </select>
            </div>
            <button 
              className="btnSend"
              onClick={editHotelHandler}
            >Send
            </button>
          </form>
        </Card>
      </PageContainer>
    </>
  );
};

export default EditHotel;
