import { useState } from "react";
import './auth.css'
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { authActions } from "../../hook/auth";
import { currentUser, saveToStorage } from "../../hook/lib";
import { useAPI } from "../../store/APIContext";

const Login = (pros) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [errorUsername, setErrorUsername] = useState('');
  const [errorPassword, setErrorPassword] = useState('');

  const [isErrorUsername, setIsErrorUsername] = useState(false);
  const [isErrorPassword, setIsErrorPassword] = useState(false);

  const { users } = useAPI();

  console.log(users);
 
  const dispatch = useDispatch();

  const navigate = useNavigate();

  const onChangeUsername = (e) => {
    setUsername(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const loginHandler = (e) => {
    e.preventDefault();

    console.log(username);

    setIsErrorUsername(false);
    setIsErrorPassword(false);

    if(!username) {
      setIsErrorUsername(true);
      setErrorUsername('username is empty');
    };

    if(password.trim() === '') {
      setIsErrorPassword(true);
      setErrorPassword('password is empty');
      return;
    };

    console.log(!users.map(i => i.username).includes(username));
    if(!isErrorUsername && !isErrorPassword) {
      if(!users.map(i => i.username).includes(username)) {
        setIsErrorUsername(true);
        setErrorUsername(username + ' does not exist');
        return;
      } else if(!users.filter(i => i.username.includes(username))[0].isAdmin) {
        setIsErrorUsername(true);
        setErrorUsername(username + ' is not admin');
        return;
      } else if(
          users.filter(i => i.username.includes(username))[0].isAdmin &&
          users.filter(i => i.username.includes(username))[0].password === password 
        ) {
        dispatch(authActions.login());
        while(currentUser.length > 0) {
          currentUser.pop();
        }
        currentUser.push(users.filter(i => i.username.includes(username))[0]);
        saveToStorage('currentUser', currentUser);
        console.log(currentUser);
      }
    }

    // navigate('/');
  };
  return <>
    <main className="centered">
      <h1>Login</h1>
      <form className="form-group">
        <input 
          type="text" 
          placeholder="username" 
          className="form-control" 
          value={username}
          onChange={onChangeUsername}
        />
        {isErrorUsername && (<span style={{ color: "red", marginBottom: "1rem" }}>{errorUsername}</span>)}
        <input 
          type="password" 
          placeholder="password" 
          className="form-control" 
          value={password}
          onChange={onChangePassword}
        />
        {isErrorPassword && (<span style={{ color: "red", marginBottom: "1rem" }}>{errorPassword}</span>)}
        <button 
          type="submit" 
          className="btn"
          onClick={loginHandler}
        >Login</button>
      </form>
    </main>
  </>
}

export default Login;
