import { useDispatch, useSelector } from 'react-redux';
import { authActions } from '../../hook/auth';
import { currentUser, removeFromStorage } from '../../hook/lib';
import './NavBar.css';

const NavBar = () => {
  const dispatch = useDispatch();
  const isAuth = useSelector(state => state.auth.isAuth);

  const logoutHandler = (e) => {
    e.preventDefault();
    dispatch(authActions.logout());
    while (currentUser.length > 0) {
      currentUser.pop();
    };
    removeFromStorage('currentUser');
  };

  return (
    <div className="spaceContent">
      {isAuth && (
        <div>{currentUser[0].email}</div>
      )}
      {isAuth && (
        <Link to="/transaction">
          <button className="navButton">Transactions</button>
        </Link>
      )}
      {isAuth && (
        <button className="navButton" onClick={logoutHandler}>Logout</button>
      )}
      {!isAuth && (
        <Link to="/auth/signup">
          <button className="navButton">Sign Up</button>
        </Link>
      )}
      {!isAuth && (
        <Link to="/auth/login">
          <button className="navButton">Login</button>
        </Link>
      )}
    </div>
  );
};

export default NavBar;
