import { useReducer } from "react";
import TransactionContext from "../context/transaction";

const initialState = {
  transaction: [],
};

const transactionReducer = (state, action) => {
  switch (action.type) {
    case "getTranaction": {
      return { transaction: action.transaction };
    };
  };
};

const TransactionProvider = (props) => {
  const [transactionState, dispatchTransactionAction] = useReducer(initialState, transactionReducer);

  const getTransaction = (transaction) => {
    dispatchTransactionAction({ type: "getTransaction", transaction });
  };

  const context = {
    transaction: transactionState.transaction,
    getTransaction,
  }

  return (
    <TransactionContext.Provider value={context}>
      {props.children}
    </TransactionContext.Provider>
  );
};

export default TransactionProvider;
