import { useState } from 'react';
import ChatRoomsAPI from '../../API/ChatRoomsAPI';
import './ChatList.css';

const ChatList = async () => {
    const [textMessage, setTextMessage] = useState('');
	  const [message, setMessage] = useState();
    const [roomId, setRoomId] = useState('');
    const cookieId = document.cookie.split('=')[1];
		//Sau đó nó emit dữ liệu lên server bằng socket với key send_message và value data
		
		// Check if text equal "/end" then end room
		if(roomId && textMessage.toLowerCase().trim() === '/end') {
			await ChatRoomsAPI.addMessage({
				message: "==END ROOM==",
				roomId: roomId,
				// is_admin: false,
				cookieId: cookieId
			});
			setTextMessage('');
			setRoomId('');
			setMessage([]);
			return;
		}

		console.log(roomId);

		//
		const data = {
			message: textMessage,
			roomId: roomId,
			// is_admin: false,
			cookieId: cookieId
		};

		//Tiếp theo nó sẽ postdata lên api đưa dữ liệu vào database
		if(roomId && textMessage.trim() !== '') {
			await ChatRoomsAPI.addMessage(data);
			setTextMessage('');
			
			setTimeout(() => {
				setLoad(true);
				socket.emit('send_message', data);
			}, 200);
		}
  return (
    <div className='ps-container ps-theme-default ps-active-y fix_scoll'>
      {message &&
        message.map((value, index) =>
          index === 0 ? (
            <div
              className='media media-chat'
              style={{ display: 'none' }}
              key={value._id}>
              {' '}
              <img
                className='avatar'
                src='https://img.icons8.com/color/36/000000/administrator-male.png'
                alt='...'
              />
              <div className='media-body' key={value.id}>
                <p>{value.message + index}</p>
              </div>
            </div>
          ) : (
            !value.is_admin ? (
              <div
                className='media media-chat media-chat-reverse'
                key={value._id}>
                <div className='media-body'>
                  <p>You: {value.message}</p>
                </div>
              </div>
            ) : (
              <div
                className='media media-chat'
                key={value._id}>
                {' '}
                <img
                  className='avatar'
                  src='https://img.icons8.com/color/36/000000/administrator-male.png'
                  alt='...'
                />
                <div className='media-body' key={value.id}>
                  <p>Cộng tác viên: {value.message}</p>
                </div>
              </div>
            )
          )
        )}
    </div>
  )
}

export default ChatList;
