import './RoomList.css';
import { useEffect, useState } from 'react';
import { useAPI } from '../../store/APIContext';
import Card from '../../UI/card/Card';
import ErrorModal from '../../UI/errorModal/ErrorModal';
import axios from "../../util/axios";
import { useNavigate } from 'react-router-dom';

const RoomList = () => {
  const [rooms, setRooms] = useAPI();
  useEffect(() => {
    async function fetchData() {
      const request = axios.get('/room');
      setRooms(request.data);
    };
    fetchData();
  }, []);

  const addNewHandler = (e) => {

  }
  return (
    <div className="listContainer">
      {/* {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={deleteHotel}
          onCancel={errorHandler}
          isDelete={error.isDelete}
        />
      )} */}
      <Card className="listCard tstCard">
        <div className="listTitle tstTitle" style={{display: 'flex', justifyContent: 'space-between'}}>
          <div>hotel list</div>
          <button className="addNew" onClick={addNewHandler}>Add New</button>
        </div>
        <table>
          <thead>
            <tr>
              <th>
                <div>
                  <input type="checkbox" name="all" id="tstAll" />
                </div>
              </th>
              <th>
                <div>ID</div>
              </th>
              <th>
                <div>Title</div>
              </th>
              <th>
                <div>Description</div>
              </th>
              <th>
                <div>Price</div>
              </th>
              <th>
                <div>Max People</div>
              </th>
              <th>
                <div>Action</div>
              </th>
            </tr>
          </thead>
          <tbody>
            {rooms && rooms.map(i => 
              <tr key={i._id}>
                <td>
                  <input type="checkbox" />
                </td>
                <td>{i._id}</td>
                <td>{i.title}</td>
                <td>{i.desc}</td>
                <td>{i.price}</td>
                <td>{i.maxPeople}</td>
                <td>
                  <button 
                    id={i._id}
                    className="delete"
                    onClick={deleteHandler}
                  >Delete</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </Card>
    </div>
  );
};

export default RoomList;
