import './RoomList.css';
import { useEffect, useState } from 'react';
import { useAPI } from '../../store/APIContext';
import Card from '../../UI/card/Card';
import ErrorModal from '../../UI/errorModal/ErrorModal';
import axios from "../../util/axios";
import { useNavigate } from 'react-router-dom';

const RoomList = () => {
  const { rooms, transactions } = useAPI();
  const [error, setError] = useState();
  const [roomId, setRoomId] = useState('');
  const [room, setRoom] = useState(rooms);

  useEffect(() => {
    async function fetchData() {
      const request = axios.get('/room');
      setRoom(request.data)
    }
    fetchData()
  }, [])

  console.log(rooms);

  const deleteHandler = (e) => {
    e.preventDefault();

    if(!transactions
        .map(i => i.room)
        .find(i => i.roomId === e.target.id)
      ) {
        setError({
          title: "Delete Room",
          message: "Are you delete this oom?",
          isDelete: true
        });
        setRoomId(e.target.id);
      } else {
        setError({
          title: "Cannot Delete Room",
          message: "This room is in use!",
          isDelete: false
        })
      }

  }

  const deleteRoom = () => {
    axios
      .post('/room', { roomId: roomId })
      // .then(res => setRooms(prev => prev.filter(i => i._id !== roomId)))
    setError(null);
  };


  const errorHandler = () => {
    setError(null);
  };

  const navigate = useNavigate();
  
  const addNewHandler = (e) => {
    e.preventDefault();
    navigate('/room/add-room');
  };

  const [page, setPage] = useState(1);

  const tstLimit = 8;
  const totalPages = Math.ceil(room.length / tstLimit);
  const pageResults = rooms.slice(page !== totalPages ? (page - 1) : (totalPages - 1)*tstLimit, tstLimit * page);

  return (
    <div className="listContainer">
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={deleteRoom}
          onCancel={errorHandler}
          isDelete={error.isDelete}
        />
      )}
      <Card className="listCard tstCard">
        <div className="listTitle tstTitle" style={{display: 'flex', justifyContent: 'space-between'}}>
          <div>room list</div>
          <button className="addNew" onClick={addNewHandler}>Add New</button>
        </div>
        <table>
          <thead>
            <tr>
              <th>
                <div>
                  <input type="checkbox" name="all" id="tstAll" />
                </div>
              </th>
              <th>
                <div>ID</div>
              </th>
              <th>
                <div>Title</div>
              </th>
              <th>
                <div>Description</div>
              </th>
              <th>
                <div>Price</div>
              </th>
              <th>
                <div>Max People</div>
              </th>
              <th>
                <div>Action</div>
              </th>
            </tr>
          </thead>
          <tbody>
            {pageResults && pageResults.map(i => 
              <tr key={i._id}>
                <td>
                  <input type="checkbox" />
                </td>
                <td>{i._id}</td>
                <td>{i.title}</td>
                <td>{i.desc}</td>
                <td>{i.price}</td>
                <td>{i.maxPeople}</td>
                <td>
                  <button 
                    id={i._id}
                    className="delete"
                    onClick={deleteHandler}
                  >Delete</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
        <div className="page">
        <div className="pageNum">
          {1+(page-1)*tstLimit} - {page === totalPages ? room.length : tstLimit+(page-1)*tstLimit} of {room.length} 
        </div>
        <div>
          <button 
            className="pagePrev" 
            onClick={() => setPage((prev) => prev - 1)}
            disabled={page === 1}
          >&lt;
          </button>
          <button 
            className="pageNext" 
            onClick={() => setPage((prev) => prev + 1)}
            disabled={page === totalPages}
          >&gt;
          </button>
        </div>
      </div>
      </Card>
    </div>
  );
};

export default RoomList;
