import './RoomList.css';
import { useContext, useEffect, useState } from 'react';
import axios from "../../util/axios";
import { useNavigate } from 'react-router-dom';
import ErrorModal from '../UI/ErrorModal';
import Card from '../UI/Card';
import APIContext from '../../store/API-context';

const RoomList = () => {
  const [error, setError] = useState();
  const [roomId, setRoomId] = useState('');
  
  const { transactions } = useContext(APIContext);
  const [rooms, setRooms] = useState([]);

  useEffect(() => {
    axios.get('/room')
      .then(res => setRooms(res.data))
      .catch(err => console.log(err));
  });

  const deleteHandler = (e) => {
    e.preventDefault();

    if(
      transactions
        .map(i => i.room.find(i => i.roomId === e.target.id))
        .filter(i => i !== undefined)
        .length === 0
    ) {
      setError({
        title: "Delete Room",
        message: "Are you delete this room?",
        isDelete: true
      });
      setRoomId(e.target.id);
    } else {
      setError({
        title: "Cannot Delete Room",
        message: "This room is in use!",
        isDelete: false,
        showOkay: true
      })
      return;
    }
  }

  const deleteRoom = () => {
    axios
      .delete('/room', { data: { roomId: roomId }})
    setError(null);
    setRooms(rooms.filter(i => i._id !== roomId));
  };


  const errorHandler = () => {
    setError(null);
  };

  const navigate = useNavigate();
  
  const addNewHandler = (e) => {
    e.preventDefault();
    navigate('/room/add-room');
  };

  const editHandler = (e) => {
    // console.log(e.target.id);
    const editRoom = rooms.filter(i => i._id === e.target.id)[0];
    navigate('/room/edit-room', { 
      state: { 
        roomId: e.target.id,
        title: editRoom.title,
        description: editRoom.desc,
        price: editRoom.price,
        maxPeople: editRoom.maxPeople,
        name: editRoom.roomNumbers,
      }
    });
  };

  const [page, setPage] = useState(1);
  const tstLimit = 8;
  const totalPages = Math.ceil(rooms.length / tstLimit);
  const pageResults = rooms.slice((page !== totalPages ? (page - 1) : (totalPages - 1))*tstLimit, tstLimit * page)

  // console.log((page !== totalPages ? (page - 1) : (totalPages - 1))*tstLimit);


  return (
    <div className="listContainer">
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onDelete={deleteRoom}
          onConfirm={errorHandler}
          onCancel={errorHandler}
          isDelete={error.isDelete}
          showOkay={error.showOkay}
        />
      )}
      <Card className="listCard tstCard">
        <div className="listTitle tstTitle" style={{display: 'flex', justifyContent: 'space-between'}}>
          <div>room list</div>
          <button className="addNew" onClick={addNewHandler}>Add New</button>
        </div>
        <table>
          <thead>
            <tr>
              <th>
                <div>
                  <input type="checkbox" name="all" id="tstAll" />
                </div>
              </th>
              <th>
                <div>ID</div>
              </th>
              <th>
                <div>Title</div>
              </th>
              <th>
                <div>Description</div>
              </th>
              <th>
                <div>Price</div>
              </th>
              <th>
                <div>Max People</div>
              </th>
              <th>
                <div>Action</div>
              </th>
              <th>
                <div>Action</div>
              </th>
            </tr>
          </thead>
          <tbody>
            {pageResults && pageResults.map(i => 
              <tr key={i._id}>
                <td>
                  <input type="checkbox" />
                </td>
                <td>{i._id}</td>
                <td>{i.title}</td>
                <td>{i.desc}</td>
                <td>{i.price}</td>
                <td>{i.maxPeople}</td>
                <td>
                  <button 
                    id={i._id}
                    className="delete"
                    onClick={deleteHandler}
                  >Delete</button>
                </td>
                <td>
                  <button 
                    id={i._id}
                    className="edit"
                    onClick={editHandler}
                  >Edit</button>
                </td>
              </tr>
            )}
            <tr>
              <td colSpan={8}>
                <div className="page">
                  <div className="pageNum"> 
                    {((1+(page-1)*tstLimit) + " - " + (page === totalPages ? rooms.length : tstLimit+(page-1)*tstLimit) + " of " + (rooms.length))}
                  </div>
                  <div>
                    <button 
                      className="pagePrev" 
                      onClick={() => setPage((prev) => prev - 1)}
                      disabled={page === 1}
                    >&lt;
                    </button>
                    <button 
                      className="pageNext" 
                      onClick={() => setPage((prev) => prev + 1)}
                      disabled={page === totalPages}
                    >&gt;
                    </button>
                  </div>
                </div>
              </td>
            </tr>
          </tbody>
        </table>
      </Card>
    </div>
  );
};

export default RoomList;
