import axios from "../../util/axios";
import { useContext, useEffect, useState } from 'react';
import './AddRoom.css';
import { useNavigate } from "react-router-dom";
import APIContext from "../../store/API-context";
import PageContainer from "../pageContainer/PageContainer";
import Card from "../UI/Card";
import Input from "../UI/Input";
import { validLength, validNumber, validRequire } from "../../util/validators";
import { setTouched } from "../hook/lib";

const AddRoom = () => {
  const { hotels } = useContext(APIContext);

  const [title, setTitle] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [description, setDescription] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [price, setPrice] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validNumber({ min: 0 }), validRequire]
  });
  const [maxPeople, setMaxPeople] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validNumber({ min: 1 }), validRequire]
  });
  const [name, setName] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const defaultHotel = "Choose a hotel"
  const [hotel, setHotel] = useState({
    value: defaultHotel,
    valid: false,
    touched: false,
    validators: [validSelect({ defaultSelect: defaultHotel })]
  });

  const navigate = useNavigate();

  const addRoomHandler = (e) => {
    e.preventDefault();

    // name.split(',').map(i => i*1).find(i => isNaN(i)) !== undefined ||
    // name.split(',').filter((v, i, arr) => arr.indexOf(v) === i).length !== name.length ||
    // name.split(',').filter(v => v !== "").length !== name.length

    setTouched(title);
    setTouched(description);
    setTouched(price);
    setTouched(maxPeople);
    setTouched(name);

    if(
      !title.valid || 
      !description.valid || 
      !price.valid || 
      !maxPeople.valid || 
      !name.valid
    ){
      return;
    }

    const addRoom = {
      title: title.split(' ').map(i => i[0].toUpperCase() + i.substring(1)).join(' '),
      description: (description.split(' ').join(' ').charAt(0).toUpperCase() + description.split(' ').join(' ').slice(1)),
      price: price,
      maxPeople: maxPeople,
      name: name.split(',').map(Number),
      hotel: hotel
    }

    axios
      .post('/room/add-room', addRoom)
      .then(res => console.log(res))
      .catch(err => console.log(err))

    console.log(name.split(',').map(Number));

    navigate('/room');
  }

  return (
    <PageContainer>
      <Card className="ahCard">
        <div className="listTitle ahListTitle">add new Room</div>
      </Card>
      <Card className="ahCardForm">
        <form className="formControl">
          <Input 
            id="title"
            isValid={isValidTitle}
            label="Title"
            type="text"
            placeholder="2 bed room"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <Input 
            id="description"
            isValid={isValidDescription}
            label="Description"
            type="text"
            placeholder="King size bed, 1 bathroom"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <Input 
            id="price"
            isValid={isValidPrice}
            label="Price"
            type="number"
            placeholder="100"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <Input 
            id="maxPeople"
            isValid={isValidMaxPeople}
            label="Max People"
            type="number"
            placeholder="2"
            value={maxPeople}
            onChange={(e) => setMaxPeople(e.target.value)}
          />
          <div className="inputFormControl">
            <div 
              className={"formLabel " + (isValidName ? "" : "labelInvalid")}
              >Rooms
            </div>
            <textarea 
              name="arRooms" 
              className={"arRooms " + (isValidName ? "" : "labelInvalid")} 
              value={name}
              onChange={(e) => setName(e.target.value)}
              rows="3"
              cols="50"
              // placeholder="Give a comma between room and numbers."
            />
          </div>
          <div className="inputFormControl">
            <div 
              className={"formLabel " + (isValidHotel ? "" : "labelInvalid")}
              >Choose a hotel
            </div>
            <select 
              name="arHotel" 
              id="arHotel"
              className={"arHotel " + (isValidHotel ? "" : "labelInvalid")} 
              value={hotel}
              onChange={(e) => setHotel(e.target.value)}
            >
              <option value="Choose a hotel">Choose a hotel</option>
              {hotels.map(i =>
                <option 
                  key={i._id}
                  value={i._id}
                >{i.name}</option>
              )}
            </select>
          </div>
          <button 
            className="btnSend"
            onClick={addRoomHandler}
          >Send
          </button>
        </form>
      </Card>
    </PageContainer>
  );
};

export default AddRoom;
