import axios from "../../util/axios";
import { useEffect, useState } from 'react';
import './AddRoom.css';
import { useNavigate } from "react-router-dom";
import PageContainer from "../pageContainer/PageContainer";
import Card from "../UI/Card";
import Input from "../UI/Input";
import { onBlurHandler, onChangeHanlder, validLength, validNumber, validRequire, validSelect } from "../../util/validators";
import { setTouched } from "../hook/lib";

const AddRoom = () => {
  const [title, setTitle] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [description, setDescription] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [price, setPrice] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validNumber({ min: 0 }), validRequire]
  });
  const [maxPeople, setMaxPeople] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validNumber({ min: 1 }), validRequire]
  });
  const [name, setName] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 3 }), validRequire]
  });
  const defaultHotel = "Choose a hotel"
  const [hotel, setHotel] = useState({
    value: defaultHotel,
    valid: false,
    touched: false,
    validators: [validSelect({ defaultSelect: defaultHotel })]
  });

  const navigate = useNavigate();

  const [hotels, setHotels] = useState([]);
  useEffect(() => {
    axios.get('/hotel')
      .then(res => setHotels(res.data))
      .catch(err => console.log(err))
  }, []);

  const addRoomHandler = (e) => {
    e.preventDefault();

    // name.split(',').map(i => i*1).find(i => isNaN(i)) !== undefined ||
    // name.split(',').filter((v, i, arr) => arr.indexOf(v) === i).length !== name.length ||
    // name.split(',').filter(v => v !== "").length !== name.length

    setTouched(setTitle);
    setTouched(setDescription);
    setTouched(setPrice);
    setTouched(setMaxPeople);
    setTouched(setName);
    setTouched(setHotel);

    console.log(typeof name.value.split(',').map(i => i.trim()))

    if(
      !title.valid || 
      !description.valid || 
      !price.valid || 
      !maxPeople.valid || 
      !name.valid ||
      !hotel.valid
    ){
      return;
    }

    setTitle(prev => ({
      ...prev,
      value: title.value.split(' ').map(i => i[0].toUpperCase() + i.substring(1)).join(' '),
    }));

    setDescription(prev => ({
      ...prev,
      value: description.value.split(' ').join(' ').charAt(0).toUpperCase() + description.value.split(' ').join(' ').slice(1),
    }));

    const addRoom = {
      title: title.value,
      description: description.value.split(' ').join(' ').charAt(0).toUpperCase() + description.value.split(' ').join(' ').slice(1),
      price: price.value,
      maxPeople: maxPeople.value,
      // name: name.value.split(',').map(Number),
      name: name.value.split(',').map(i => i.trim()),
      hotel: hotel.value
    }

    console.log(addRoom);

    axios
      .post('/room/add-room', addRoom)
      .then(res => console.log(res))
      .catch(err => console.log(err))

    // console.log(name.split(',').map(Number));

    navigate('/room');
  }

  return (
    <PageContainer>
      <Card className="ahCard">
        <div className="listTitle ahListTitle">add new Room</div>
      </Card>
      <Card className="ahCardForm">
        <form className="formControl">
          <Input 
            control="input"
            id="title"
            type="text"
            placeholder="2 bed room"
            label="Title"
            required={true}
            onChange={(e) => onChangeHanlder(e, setTitle, title.validators)}
            onBlur={e => onBlurHandler(e, setTitle, title.validators)}
            value={title.value}
            isValid={title.valid}
            isTouched={title.touched}
          />
          <Input 
            control="input"
            id="description"
            type="text"
            placeholder="King size bed, 1 bathroom"
            label="Description"
            required={true}
            onChange={(e) => onChangeHanlder(e, setDescription, description.validators)}
            onBlur={e => onBlurHandler(e, setDescription, description.validators)}
            value={description.value}
            isValid={description.valid}
            isTouched={description.touched}
          />
          <Input 
            control="input"
            id="price"
            type="number"
            placeholder="100"
            label="Price"
            required={true}
            onChange={(e) => onChangeHanlder(e, setPrice, price.validators)}
            onBlur={e => onBlurHandler(e, setPrice, price.validators)}
            value={price.value}
            isValid={price.valid}
            isTouched={price.touched}
          />
          <Input 
            control="input"
            id="maxPeople"
            type="number"
            placeholder="2"
            label="Max People"
            required={true}
            onChange={(e) => onChangeHanlder(e, setMaxPeople, maxPeople.validators)}
            onBlur={e => onBlurHandler(e, setMaxPeople, maxPeople.validators)}
            value={maxPeople.value}
            isValid={maxPeople.valid}
            isTouched={maxPeople.touched}
          />
          <Input 
            control="textarea"
            id="name"
            type="textarea"
            placeholder="Give a comma between room and numbers."
            label="Rooms"
            required={true}
            onChange={(e) => onChangeHanlder(e, setName, name.validators)}
            onBlur={e => onBlurHandler(e, setName, name.validators)}
            value={name.value}
            isValid={name.valid}
            isTouched={name.touched}
            rows="3"
            cols="50"
            inputClassName="arRooms"
          />
          <div className="inputFormControl">
            <div 
              className={"form-label"}
              >Choose a hotel
            </div>
            <select 
              name="arHotel" 
              id="arHotel"
              className={[
                "arHotel",
                (hotel.valid ? "" : "hotel-invalid"),
                (hotel.touched ? "touched" : "untouched")
              ].join(' ')} 
              value={hotel.value}
              onChange={(e) => onChangeHanlder(e, setHotel, hotel.validators)}
            >
              <option hidden value={defaultHotel}>Choose a hotel</option>
              {hotels.map(i =>
                <option 
                  key={i._id}
                  value={i._id}
                >{i.name}</option>
              )}
            </select>
          </div>
          <button 
            className="btnSend"
            onClick={addRoomHandler}
          >Send
          </button>
        </form>
      </Card>
    </PageContainer>
  );
};

export default AddRoom;
