import './Input.css'

const Input = (props) => {
  return (
    <div className={"inputFormControl " + props.className}>
      <label
        htmlFor={props.id}
        className={"formLabel " + (props.isValid ? "" : "labelInvalid")}
      >
        {props.label}
      </label>
      <input
        type={props.type}
        id={props.id}
        placeholder={props.placeholder}
        className={"formInput " + (props.isValid ? "" : "inputInvalid")}
        value={props.value}
        onChange={props.onChange}
      />
    </div>
  );
};

export default Input;
