const initialState = {
	idUser: '',
	role: ''
};

const ReducerSession = (state = initialState, action) => {
	switch (action.type) {
		case 'ADD_SESSION':
			return { idUser: action._id, role: action.role };

		case 'DELETE_SESSION':
			return { idUser: '', role: '' };

		default:
			return state;
	}
};

export default ReducerSession;
