import { useContext, useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import ErrorModal from '../UI/ErrorModal';
import Card from '../UI/Card';
import ProductAPI from '../../API/ProductAPI';

const ProductList = () => {
  const [error, setError] = useState();
  const [productId, setProductId] = useState('');
  const [products, setProducts] = useState([]);

  useEffect(() => {
    ProductAPI.getAPI()
      .then(res => {
        console.log(res)
        setProducts(res);
      });
  }, []);

  // console.log(products);

  const deleteHandler = (e) => {
    e.preventDefault();

    // if(
    //   transactions
    //     .map(i => i.hotel)
    //     .filter(i => i._id === e.target.id)
    //     .length === 0
    // ) {
    //   setError({
    //     title: "Delete Hotel",
    //     message: "Are you delete this hotel?",
    //     isDelete: true
    //   });
    //   setProductId(e.target.id);
    // } else {
    //   setError({
    //     title: "Cannot Delete Hotel",
    //     message: "This hotel is in use!",
    //     isDelete: false,
    //     showOkay: true
    //   })
    //   return;
    // }
  }

  const deleteHotel = () => {
    // axios
    //   .delete('/hotel', { data: { productId: productId }})
    //   .then(i => {
    //     axios.get('/hotel')
    //       .then(res => setProducts(res.data))
    //       .catch(err => console.log(err));
    //   })
    setError(null);
  };

  const errorHandler = () => {
    setError(null);
  };

  const navigate = useNavigate();
  
  const addNewHandler = (e) => {
    e.preventDefault();
    navigate('/hotel/add-hotel');
  }

  const editHandler = (e) => {
    // console.log(e.target.id);
    const editHotel = products.filter(i => i._id === e.target.id)[0];
    console.log(editHotel);
    // navigate('/hotel/edit-hotel', {
    //   state: {
    //     productId: e.target.id,
    //     name: editHotel.name,
    //     title: editHotel.title,
    //     city: editHotel.city,
    //     address: editHotel.address,
    //     distance: editHotel.distance,
    //     description: editHotel.desc,
    //     price: editHotel.cheapestPrice,
    //     images: editHotel.photos,
    //     featured: editHotel.featured,
    //     type: editHotel.type,
    //     rooms: editHotel.rooms
    //   }
    // });
  };

  return (
    <div className="listContainer">
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onDelete={deleteHotel}
          onConfirm={errorHandler}
          onCancel={errorHandler}
          isDelete={error.isDelete}
          showOkay={error.showOkay}
        />
      )}
      <Card className="listCard tstCard">
        <div className="listTitle tstTitle" style={{display: 'flex', justifyContent: 'space-between'}}>
          <div>hotel list</div>
          <button className="addNew" onClick={addNewHandler}>Add New</button>
        </div>
        <table>
          <thead>
            <tr>
              <th>
                <div>
                  <input type="checkbox" name="all" id="tstAll" />
                </div>
              </th>
              <th>
                <div>ID</div>
              </th>
              <th>
                <div>Name</div>
              </th>
              <th>
                <div>Price</div>
              </th>
              <th>
                <div>Image</div>
              </th>
              <th>
                <div>Category</div>
              </th>
              <th>
                <div>Action</div>
              </th>
              {/* <th>
                <div>Action</div>
              </th> */}
            </tr>
          </thead>
          <tbody>
            {products && products.map(i => 
              <tr key={i._id}>
                <td>
                  <input type="checkbox" />
                </td>
                <td>{i._id}</td>
                <td>{i.name}</td>
                <td>{i.type}</td>
                <td>{i.title}</td>
                <td>{i.city}</td>
                <td>
                  <button 
                    id={i._id}
                    className="delete"
                    onClick={deleteHandler}
                  >Delete</button>
                {/* </td> */}
                {/* <td> */}
                  <button 
                    id={i._id}
                    className="edit"
                    onClick={editHandler}
                  >Update</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </Card>
    </div>
  );
};

export default ProductList;
