import { useDispatch, useSelector } from 'react-redux';
import { Link, useNavigate } from 'react-router-dom';
import { authActions } from '../hook/auth';
import { idUser, removeFromStorage } from '../hook/lib';
import './NavBar.css';
import { deleteSession } from "../Redux/Action/ActionSession";

const NavBar = (props) => {
  const dispatch = useDispatch();
  // const isAuth = useSelector(state => state.auth.isAuth);
  const navigate = useNavigate();
  const idUser = localStorage.getItem('id_user');

  const logoutHandler = (e) => {
    // e.preventDefault();
    // dispatch(authActions.logout());
    // while (idUser.length > 0) {
    //   idUser.pop();
    // };
    // removeFromStorage('id_user');
    // navigate('/auth/login');
    localStorage.clear();
		
		const action = deleteSession('');
		dispatch(action);
    navigate('/auth/login');
  };

  // console.log(currentUser);
  // console.log(isAuth);

  return (
    <div className={`spaceContent navItems`}>
      {isAuth && (
        <div>{idUser[0].email}</div>
      )}
      {isAuth && (
        <button className="navButton" onClick={logoutHandler}>Logout</button>
      )}
      {!isAuth && (
        <Link to="/auth/signup">
          <button className="navButton">Sign Up</button>
        </Link>
      )}
      {!isAuth && (
        <Link to="/auth/login">
          <button className="navButton">Login</button>
        </Link>
      )}
    </div>
  );
};

export default NavBar;
