import { useState } from 'react';
import Card from '../../UI/card/Card';
import Input from '../../UI/input/Input';
import NavBar from '../navbar/NavBar';
import SideBar from '../sidebar/SideBar';
import './AddHotel.css';

const AddHotel = () => {
  const [name, setName] = useState('');
  const [isValidName, setIsValidName] = useState(true);
  const [type, setType] = useState('');
  const [isValidType, setIsValidType] = useState(true);
  const [city, setCity] = useState('');
  const [isValidCity, setIsValidCity] = useState(true);
  const [address, setAddress] = useState('');
  const [isValidAddress, setIsValidAddress] = useState(true);
  const [distance, setDistance] = useState('');
  const [isValidDistance, setIsValidDistance] = useState(true);
  const [title, setTitle] = useState('');
  const [isValidTitle, setIsValidTitle] = useState(true);
  const [description, setDescription] = useState('');
  const [isValidDescription, setIsValidDescription] = useState(true);
  const [price, setPrice] = useState('');
  const [isValidPrice, setIsValidPrice] = useState(true);
  const [images, setImages] = useState('');
  const [isValidImages, setIsValidImages] = useState(true);
  const [featured, setFeatured] = useState('');
  const [isValidFeatured, setIsValidFeatured] = useState(true);
  const [rooms, setRooms] = useState('');
  const [isValidRooms, setIsValidRooms] = useState(true);


  return (
    <div className="container">
    <SideBar />
    <div>
      <NavBar />
      <div className="pageContent">
        <Card className="ahCard">
          <div className="listTitle ahListTitle">add new hotel</div>
        </Card>
        <Card className="ahCardForm">
          <form className="formControl">
            <Input 
              id="name"
              isValid={isValidName}
              label="Name"
              type="text"
              placeholder="My Hotel"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <Input 
              id="type"
              isValid={isValidType}
              label="Type"
              type="text"
              placeholder="hotel"
              value={type}
              onChange={(e) => setType(e.target.value)}
            />
            <Input 
              id="city"
              isValid={isValidCity}
              label="City"
              type="text"
              placeholder="New York"
              value={city}
              onChange={(e) => setCity(e.target.value)}
            />
            <Input 
              id="address"
              isValid={isValidAddress}
              label="Address"
              type="text"
              placeholder="eiton st. 216"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
            <Input 
              id="distance"
              isValid={isValidDistance}
              label="Distance form City Center"
              type="number"
              placeholder="500"
              value={distance}
              onChange={(e) => setDistance(e.target.value)}
            />
            <Input 
              id="title"
              isValid={isValidTitle}
              label="Title"
              type="text"
              placeholder="The best hotel"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <Input 
              id="description"
              isValid={isValidDescription}
              label="Description"
              type="text"
              placeholder="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <Input 
              id="price"
              isValid={isValidPrice}
              label="Price"
              type="number"
              placeholder="100"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
            <div className="inputFormControl">
              <div 
                className={"formLabel " + (isValidImages ? "" : "labelInvalid")}
                >Images
              </div>
              <textarea 
                name="ahImages" 
                className={"ahImages " + (isValidImages ? "" : "labelInvalid")} 
                value={images}
                onChange={(e) => setImages(e.target.value)}
                rows="3"
                cols="50"
              />
            </div>
            <div className="inputFormControl">
              <div 
                className={"formLabel " + (isValidFeatured ? "" : "labelInvalid")}
                >Featured
              </div>
              <select 
                name="ahFeatured" 
                id="ahFeatured"
                className={"ahFeatured " + (isValidFeatured ? "" : "labelInvalid")} 
                value={featured}
                onChange={(e) => setFeatured(e.target.value)}
              >
                <option value={false}>No</option>
                <option value={true}>Yes</option>
              </select>
            </div>
            <div className="inputFormControl ahRooms">
              <div 
                className={"formLabel " + (isValidRooms ? "" : "labelInvalid")}
                >Rooms
              </div>
              <textarea 
                name="ahRooms" 
                className={(isValidRooms ? "" : "labelInvalid")} 
                value={rooms}
                onChange={(e) => setRooms(e.target.value)}
                rows="6"
                cols="50"
              />
            </div>
            <button ></button>
          </form>
        </Card>
      </div>
    </div>
  </div>
  );
};

export default AddHotel;
