import axios from "../../util/axios";
import { useState } from "react";
import Navbar from "../navbar/Navbar"
import './auth.css'
import { useDispatch } from "react-redux";
import { authActions } from "../hook/auth";
import { currentUser, saveToStorage, setTouched } from "../hook/lib";
import { useLocation, useNavigate } from "react-router-dom";
import Input from "../UI/Input";
import ErrorModal from "../UI/ErrorModal";
import { validLength, validRequire } from "../../util/validators";

const Login = (pros) => {
  const [username, setUsername] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [password, setPassword] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });

  const [error, setError] = useState();
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const location = useLocation();
  console.log(location);

  const onChangeHanlder = (e, setInput, validators) => {
    const value = e.target.value;
    let valid = true;
    for(const validator of validators) {
      valid = valid && validator(value);
    }
    setInput(prev => ({
      ...prev,
      value: value,
      valid: valid
    }));
  };

  const onBlurHandler = (e, set) => {
    set(prev => ({
      ...prev,
      touched: true
    }));
  };

  const loginHandler = (e) => {
    e.preventDefault();

    setTouched(setUsername);
    setTouched(setPassword);

    if(username.valid) {
      axios.post('/user/login/admin', {
        username: username.value,
        password: password.value,
      })
      .then(res => {
        if(res.status === 200 || res.status === 201) {
          // console.log(res);
          dispatch(authActions.login());
          while(currentUser.length > 0) {
            currentUser.pop();
          };
          currentUser.push(res.data);
          saveToStorage('currentUser', currentUser);
        }
      })
      .then(result => {
        if(!location.state) {
          navigate('/');
        } else {
          navigate(location.state.pathname);
        }
        console.log(location);
      })
      .catch(err => {
        setError({
          title: 'Validation failed!',
          message: err.response.data.message
        })
      });
    };

    if(!username.valid || !password.valid) {
      // console.log('no');
      return setError({
        title: 'Validation failed!',
        message: 'Invalid username or password!'
      })
    }
  };

  return <>
    <Navbar/>
    <>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={() => setError(null)}
          error={!!error}
        />
      )}
      <main className="centered">
        
        <h1>Login</h1>
        <form className="form-group">
          <Input
            control="input"
            id="username"
            type="text" 
            placeholder="username" 
            required={true}
            onChange={e => onChangeHanlder(e, setUsername, username.validators)}
            onBlur={e => onBlurHandler(e, setUsername)}
            value={username.value}
            isValid={username.valid}
            isTouched={username.touched}
          />
          <Input 
            control="input"
            id="password"
            type="password" 
            placeholder="password"
            onChange={e => onChangeHanlder(e, setPassword, password.validators)}
            onBlur={e => onBlurHandler(e, setPassword)}
            required={true}
            value={password.value}
            isValid={password.valid}
            isTouched={password.touched}
          />
          <button 
            type="submit" 
            className="btn"
            onClick={loginHandler}
          >Login</button>
        </form>
      </main>
    </>
  </>
}

export default Login;
