import axios from "../../util/axios";
import { useState } from 'react';
import { useAPI } from '../../store/APIContext';
import Card from '../../UI/card/Card';
import Input from '../../UI/input/Input';
import NavBar from '../navbar/NavBar';
import SideBar from '../sidebar/SideBar';
import './AddHotel.css';
import { useNavigate } from "react-router-dom";

const AddHotel = () => {
  const [name, setName] = useState('');
  const [isValidName, setIsValidName] = useState(true);
  const [type, setType] = useState('');
  const [isValidType, setIsValidType] = useState(true);
  const [city, setCity] = useState('');
  const [isValidCity, setIsValidCity] = useState(true);
  const [address, setAddress] = useState('');
  const [isValidAddress, setIsValidAddress] = useState(true);
  const [distance, setDistance] = useState('');
  const [isValidDistance, setIsValidDistance] = useState(true);
  const [title, setTitle] = useState('');
  const [isValidTitle, setIsValidTitle] = useState(true);
  const [description, setDescription] = useState('');
  const [isValidDescription, setIsValidDescription] = useState(true);
  const [price, setPrice] = useState('');
  const [isValidPrice, setIsValidPrice] = useState(true);
  const [images, setImages] = useState('');
  const [isValidImages, setIsValidImages] = useState(true);
  const [featured, setFeatured] = useState('');
  const [isValidHotelRooms, setIsValidHotelRooms] = useState(true);
  const [selectedHotelRooms, setSelectedHotelRooms] = useState([]);
  
  const navigate = useNavigate();

  const { rooms } = useAPI();
  // console.log(rooms);

  const hotelRoomsSelectHandler = (e) => {
    const updatedOptions = [...e.target.options]
      .filter(option => option.selected)
      .map(x => x.value);
    // console.log('updatedOptions', updatedOptions);
    setSelectedHotelRooms(updatedOptions);
  }

  const addHotelHandler = (e) => {
    e.preventDefault();

    if(!name) {
      setIsValidName(false);
    } else {
      setIsValidName(true);
    };
    if(!type) {
      setIsValidType(false);
    } else {
      setIsValidType(true);
    };
    if(!city) {
      setIsValidCity(false);
    } else {
      setIsValidCity(true);
    };
    if(!address) {
      setIsValidAddress(false);
    } else {
      setIsValidAddress(true);
    };
    if(!distance || +distance < 0) {
      setIsValidDistance(false);
    } else {
      setIsValidDistance(true);
    };
    if(!title) {
      setIsValidTitle(false);
    } else {
      setIsValidTitle(true);
    };
    if(!description) {
      setIsValidDescription(false);
    } else {
      setIsValidDescription(true);
    };
    if(!price || +price <= 0) {
      setIsValidPrice(false);
    } else {
      setIsValidPrice(true);
    };
    if(!images) {
      setIsValidImages(false);
    } else {
      setIsValidImages(true);
    };
    if(selectedHotelRooms.length === 0) {
      setIsValidHotelRooms(false);
    } else {
      setIsValidHotelRooms(true);
    };

    if(
      !isValidName || 
      !isValidType ||
      !isValidCity ||
      !isValidAddress ||
      !isValidDistance ||
      !isValidTitle ||
      !isValidDescription ||
      !isValidPrice ||
      !isValidImages ||
      !isValidHotelRooms
    ) {
      return;
    }

    console.log(city.split(' ')[0][0]);

    const addHotel = {
      name: name,
      type: type,
      city: city,
      address: address,
      distance: distance,
      title: title,
      description: description,
      price: price,
      images: images,
      featured: featured,
      rooms: rooms
    }

    axios
      .post('/hotel', addHotel)
      .then(res => console.log(res))
      .catch(err => console.log(err))
    
    // navigate('/hotel');
  }

  return (
    <div className="container">
    <SideBar />
    <div>
      <NavBar />
      <div className="pageContent">
        <Card className="ahCard">
          <div className="listTitle ahListTitle">add new hotel</div>
        </Card>
        <Card className="ahCardForm">
          <form className="formControl">
            <Input 
              id="name"
              isValid={isValidName}
              label="Name"
              type="text"
              placeholder="My Hotel"
              value={name}
              onChange={(e) => setName(e.target.value)}
            />
            <Input 
              id="type"
              isValid={isValidType}
              label="Type"
              type="text"
              placeholder="hotel"
              value={type}
              onChange={(e) => setType(e.target.value)}
            />
            <Input 
              id="city"
              isValid={isValidCity}
              label="City"
              type="text"
              placeholder="New York"
              value={city}
              onChange={(e) => setCity(e.target.value)}
            />
            <Input 
              id="address"
              isValid={isValidAddress}
              label="Address"
              type="text"
              placeholder="eiton st. 216"
              value={address}
              onChange={(e) => setAddress(e.target.value)}
            />
            <Input 
              id="distance"
              isValid={isValidDistance}
              label="Distance form City Center"
              type="number"
              placeholder="500"
              value={distance}
              onChange={(e) => setDistance(e.target.value)}
            />
            <Input 
              id="title"
              isValid={isValidTitle}
              label="Title"
              type="text"
              placeholder="The best hotel"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
            />
            <Input 
              id="description"
              isValid={isValidDescription}
              label="Description"
              type="text"
              placeholder="description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
            />
            <Input 
              id="price"
              isValid={isValidPrice}
              label="Price"
              type="number"
              placeholder="100"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
            />
            <div className="inputFormControl">
              <div 
                className={"formLabel " + (isValidImages ? "" : "labelInvalid")}
                >Images
              </div>
              <textarea 
                name="ahImages" 
                className={"ahImages " + (isValidImages ? "" : "labelInvalid")} 
                value={images}
                onChange={(e) => setImages(e.target.value)}
                rows="3"
                cols="50"
              />
            </div>
            <div className="inputFormControl">
              <div 
                className={"formLabel "}
                >Featured
              </div>
              <select 
                name="ahFeatured" 
                id="ahFeatured"
                className={"ahFeatured "} 
                value={featured}
                onChange={(e) => setFeatured(e.target.value)}
              >
                <option value={false}>No</option>
                <option value={true}>Yes</option>
              </select>
            </div>
            <div className="inputFormControl ahHotelRooms">
              <div 
                className={"formLabel " + (isValidHotelRooms ? "" : "labelInvalid")}
                >Rooms (multiple select: ctrl + click)
              </div>
              <select 
                name="ahHotelRooms" 
                id="ahFeatured"
                className={"ahHotelRooms " + (isValidHotelRooms ? "" : "labelInvalid")} 
                value={selectedHotelRooms}
                onChange={hotelRoomsSelectHandler}
                multiple
              >
                {rooms.map(r => 
                  <option value={r._id} key={r._id}>{r.desc}</option>
                )}
              </select>
            </div>
            <button 
              className="btnSend"
              onClick={addHotelHandler}
            >Send
            </button>
          </form>
        </Card>
      </div>
    </div>
  </div>
  );
};

export default AddHotel;
