import queryString from 'query-string';
import { useEffect, useState } from 'react';
import ChatRoomsAPI from '../../API/ChatRoomsAPI';
import { onBlurHandler, onChangeHanlder, valid } from '../../util/validators';
import Input from '../UI/Input';
import './ChatList.css';

const ChatList = () => {
  const [chatSearch, setChatSearch] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [valid({ type: true })]
  });
  const [chatList, setChatList] = useState([]);
  const [message, setMessage] = useState([]);
  const [textMessage, setTextMessage] = useState('');
  const [roomId, setRoomId] = useState('');
  const [load, setLoad] = useState(false);
  const cookieId = document.cookie.split('=')[1];

  console.log(chatSearch.value);

  useEffect(() => {
    const fetchData = async() => {
      const params = {
        cookieId: cookieId,
        chatSearch: chatSearch.value
      }
      const query = '?' + queryString.stringify(params);
      const response = await ChatRoomsAPI.searchMessage(query);
      setChatList(response);
    }
    fetchData();
  }, [chatSearch])
  console.log(chatSearch.value);
  console.log(chatList);

  const onChat = async (e) => {
    e.preventDefault();
    setRoomId(e.target.key);
    const response = await ChatRoomsAPI.getMessageByRoomId(roomId);
    setMessage(response.content);
  }

  return (
    <div>
      <h1>Chat</h1>
      <em>Apps / Chat</em>
      <div className='bg-white container'>
        <div className="chatInfo flex-column">
          <div className="chatSearch">
            <Input 
              control='input'
              type='search'
              id='chatSearch'
              placeholder='Search Contact'
              required={true}
              onChange={e => onChangeHanlder(e, setChatSearch, chatSearch.validators)}
              onBlur={e => onBlurHandler(e, setChatSearch, chatSearch.validators)}
              value={chatSearch.value}
              isValid={chatSearch.valid}
              isTouched={chatSearch.touched}
              className='chatListItem'
            />
            {chatList && chatList.map(value => 
              <div 
                key={value._id} 
                className='flex chatListItem'
                onClick={onChat}
              >
                <img
                  className='avatar'
                  src='https://img.icons8.com/color/36/000000/administrator-male.png'
                  alt='...'
                />
                <div>
                  <p>{value._id}</p>
                </div>
              </div>
            )}
          </div>
          <div className="chatBox flex-column">
            <div className="chatMedia">
              {message && message.map(value =>
                !value.is_admin ? (
                  <div
                    className='media media-chat media-chat-reverse'
                    key={value._id}>
                    <div className='media-body'>
                      <p>You: {value.message}</p>
                    </div>
                  </div>
                ) : (
                  <div
                    className='media media-chat'
                    key={value._id}>
                    {' '}
                    <img
                      className='avatar'
                      src='https://img.icons8.com/color/36/000000/administrator-male.png'
                      alt='...'
                    />
                    <div className='media-body' key={value.id}>
                      <p>Cộng tác viên: {value.message}</p>
                    </div>
                  </div>
                )
              )}
            </div>
            <div className="chatField"></div>
          </div>   
        </div>
      </div>
    </div>
  )
};

export default ChatList;
