import axios from "../../util/axios";
import { useState } from "react"
import './auth.css'
import { useNavigate } from "react-router-dom";
import { useAPI } from "../../store/APIContext";

const SignUp = (pros) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [errorUsername, setErrorUsername] = useState('');
  const [errorPassword, setErrorPassword] = useState('');

  const [isValidUsername, setIsValidUsername] = useState(true);
  const [isValidPassword, setIsValidPassword] = useState(true);

  const { users } = useAPI();
  
  const navgigate = useNavigate('/');

  const onChangeUsername = (e) => {
    setUsername(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const signUpHandler = (e) => {
    e.preventDefault();

    setIsValidUsername(false);
    setIsValidPassword(false);

    if(username.trim() === '') {
      setIsValidUsername(false);
      setErrorUsername('username is empty');
    } else if(users.find(i => i.username === username)) {
      setIsValidUsername(false);
      setErrorUsername(username + ' is exist');
    } else {
      setIsValidUsername(true);
    }

    if(password.trim() === '') {
      setIsValidPassword(false);
      setErrorPassword('password is empty');
    } else {
      setIsValidPassword(true);
    }

    console.log('u '+username)
    console.log('u '+errorUsername)
    console.log('p '+password)
    console.log('p '+errorPassword)
    
    if (!isValidUsername ||!isValidPassword) {
      console.log('no')
      return;
    }

    if(isValidUsername && isValidPassword) {
      // axios
      //   .post('/user', {
      //       username: username,
      //       password: password,
      //       isAdmin: true
      //     })
      //   .then(res => console.log(res))
      //   .catch(err => console.log(err))

      // navgigate('/');
      console.log('ok')
    }
  };

  return <>
    <main className="centered">
      <h1>Sign Up</h1>
      <form className="form-group">
        <input 
          type="text" 
          placeholder="username" 
          className="form-control" 
          value={username}
          onChange={onChangeUsername}
        />
        {!isValidUsername && <span style={{ color: "red", marginBottom: "1rem" }}>{errorUsername}</span>}
        <input 
          type="password" 
          placeholder="password" 
          className="form-control" 
          value={password}
          onChange={onChangePassword}
        />
        {!isValidPassword && <span style={{ color: "red", marginBottom: "1rem" }}>{errorPassword}</span>}
        <button 
          type="submit" 
          className="btn"
          onClick={signUpHandler}
        >
          Create Account
        </button>
      </form>
    </main>
  </>
}

export default SignUp;
