import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faFile,
  faUser,
  faHotel,
  faBox,
  faTruck,
  faRightToBracket
} from "@fortawesome/free-solid-svg-icons";
import "./SideBar.css";
import { NavLink, useNavigate } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { authActions } from "../hook/auth";
import { currentUser, removeFromStorage } from "../hook/lib";

const SideBar = () => {
  const isAuth = useSelector(state => state.auth.isAuth);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const logoutHandler = () => {
    dispatch(authActions.logout());
    while (currentUser.length > 0) {
      currentUser.pop();
    };
    removeFromStorage('currentUser');
    navigate('/auth/login');
  }

  return (
    <div className="sideBarContainer">
      <div className="sbSubContainer">
        <div className="sbHeader">
          <h1>Admin Page</h1>
        </div>
        {isAuth && (
          <div className="sbListContainer">
            <div className="sbListContent">
              <div className="sbListTitle">
                main
              </div>
              <ul className="sbList">
                <li className="sbListItem">
                  <FontAwesomeIcon icon={faFile} className="sbIcon" />
                  <NavLink 
                    to="/home" 
                    className={({ isActive }) => (isActive ? "active" : "")}
                    onClick={(e) => isAuth ? "" : e.preventDefault()}
                  >
                    <div>dashboard</div>
                  </NavLink>
                </li>
              </ul>
            </div>
            <div className="sbListContent">
              <div className="sbListTitle">
                list
              </div>
              <ul className="sbList">
                <li className="sbListItem">
                  <FontAwesomeIcon icon={faUser} className="sbIcon" />
                  {/* <NavLink 
                    to="/user" 
                    className={({ isActive }) => (isActive ? "active" : "")}
                    onClick={(e) => isAuth ? "" : e.preventDefault()}
                  > */}
                    <div>users</div>
                  {/* </NavLink> */}
                </li>
                <li className="sbListItem">
                  <FontAwesomeIcon icon={faBox} className="sbIcon" />
                  <NavLink 
                    to="/product" 
                    className={({ isActive }) => (isActive ? "active" : "")}
                    onClick={(e) => isAuth ? "" : e.preventDefault()}
                  >
                    <div>products</div>
                  </NavLink>
                </li>
              </ul>
            </div>
            {/* <div className="sbListContent">
              <div className="sbListTitle">
                new
              </div>
              <ul className="sbList">
                <li className="sbListItem">
                  <FontAwesomeIcon icon={faHotel} className="sbIcon" />
                  <NavLink 
                    to="/hotel/add-hotel" 
                    className={({ isActive }) => (isActive ? "active" : "")}
                    onClick={(e) => isAuth ? "" : e.preventDefault()}
                  >
                    <div>new hotel</div>
                  </NavLink>
                </li>
                <li className="sbListItem">
                  <FontAwesomeIcon icon={faBox} className="sbIcon" />
                  <NavLink 
                    to="/room/add-room" 
                    className={({ isActive }) => (isActive ? "active" : "")}
                    onClick={(e) => isAuth ? "" : e.preventDefault()}
                  >
                    <div>new room</div>
                  </NavLink>
                </li>
              </ul>
            </div> */}
            <div className="sbListContent">
              <div className="sbListTitle">
                user
              </div>
              <ul className="sbList">
                <li className="sbListItem">
                  <FontAwesomeIcon icon={faRightToBracket} className="sbIcon" />
                  <div className="sbLogout" onClick={logoutHandler}>logout</div>
                  </li>
              </ul>
            </div>
          </div>
        )}
      </div>
    </div>
  )
}

export default SideBar;
