import axios from "axios";
import { useContext, useEffect, useState } from 'react';
import './AddProduct.css';
import { useNavigate } from "react-router-dom";
import PageContainer from "../pageContainer/PageContainer";
import Card from "../UI/Card";
import Input from "../UI/Input";
import { onBlurHandler, onChangeHanlder, validLength, validNumber, validRequire } from "../../util/validators";
import { setTouched } from "../hook/lib";
import ErrorModal from "../UI/ErrorModal";
import FilePicker from "../UI/FilePicker";

const AddProduct = () => {
  const [name, setName] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [category, setCategory] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [shortDesc, setShortDesc] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [longDesc, setLongDesc] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [images, setImages] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  
  const [error, setError] = useState();
  const navigate = useNavigate();

  const addHandler = (e) => {
    e.preventDefault();

    setTouched(setName);
    setTouched(setCategory);
    setTouched(setShortDesc);
    setTouched(setLongDesc);
    setTouched(setImages);

    if(
      !name.valid ||
      !category.valid ||
      !shortDesc.valid ||
      !longDesc.valid
    ) {
      const params = {
        category: category,
        img1: images[0],
        img2: images[1],
        img3: images[2],
        img4: images[3],
        img5: images[4],
        long_desc: longDesc,
        name: name,
        price: Math.random(),
        short_desc: shortDesc
      }
    }
  }

  return (
    <>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={() => setError(null)}
          error={!!error}
        />
      )}
      <PageContainer>
        <Card className="ahCard">
          <div className="listTitle ahListTitle">add product</div>
        </Card>
        <Card className="ahCardForm">
          <form className="ahFormControl">
            <Input 
              control="input"
              id="name"
              type="text"
              placeholder="Enter Product Name"
              label="Product Name"
              required={true}
              onChange={e => onChangeHanlder(e, setName, name.validators)}
              onBlur={e => onBlurHandler(e, setName, name.validators)}
              value={name.value}
              isValid={name.valid}
              isTouched={name.touched}
            />
            <Input 
              control="input"
              id="category"
              type="text"
              placeholder="Enter Category"
              label="Category"
              required={true}
              onChange={e => onChangeHanlder(e, setCategory, category.validators)}
              onBlur={e => onBlurHandler(e, setCategory, category.validators)}
              value={category.value}
              isValid={category.valid}
              isTouched={category.touched}
            />
            <Input 
              control="textarea"
              id="shortDesc"
              type="text"
              placeholder="Enter Short Description"
              label="Short Description"
              required={true}
              onChange={e => onChangeHanlder(e, setShortDesc, shortDesc.validators)}
              onBlur={e => onBlurHandler(e, setShortDesc, shortDesc.validators)}
              value={shortDesc.value}
              isValid={shortDesc.valid}
              isTouched={shortDesc.touched}
              rows={5}
              cols={5}
            />
            <Input 
              control="textarea"
              id="longDesc"
              type="text"
              placeholder="Enter Long Description"
              label="Long Description"
              required={true}
              onChange={e => onChangeHanlder(e, setLongDesc, longDesc.validators)}
              onBlur={e => onBlurHandler(e, setLongDesc, longDesc.validators)}
              value={longDesc.value}
              isValid={longDesc.valid}
              isTouched={longDesc.touched}
              rows={5}
              cols={5}
            />
            <FilePicker
              control="input"
              id="images"
              type="text"
              placeholder="Enter Images"
              label="Upload image (5 images)"
              required={true}
              onChange={e => onChangeHanlder(e, setImages, images.validators)}
              onBlur={e => onBlurHandler(e, setImages, images.validators)}
              value={images.value}
              isValid={images.valid}
              isTouched={images.touched}
            />
            <button 
              className="btnSend"
              onClick={addHandler}
            >Submit
            </button>
          </form>
        </Card>
      </PageContainer>
    </>
  );
};

export default AddProduct;
