import { useContext, useEffect, useState } from "react";
import {
  BrowserRouter,
  Routes,
  Route,
} from "react-router-dom";
import "./App.css";
import Home from "./pages/home/Home";
import TransactionContext from "./store/context/transaction";
import axios from "./store/util/axios";

function App() {
  const [transactions, setTtransactions] = useState([]);
  const tstCtx = useContext(TransactionContext);
  useEffect(() => {
    async function fetchData() {
      const request = await axios.get('/transaction');
      setTtransactions(request.data);
      return;
    };
    fetchData();
  }, []);
  tstCtx.getTranaction(transactions);
  console.log(tstCtx.transactions);
  console.log(transactions);
  return (
    <BrowserRouter>
      <Routes>
        <Route path="/" element={<Home/>}/>
      </Routes>
    </BrowserRouter>
  );
}

export default App;
