import axios from "../../util/axios";
import { useContext, useState } from 'react';
import './AddHotel.css';
import { useNavigate } from "react-router-dom";
import APIContext from "../../store/API-context";
import PageContainer from "../pageContainer/PageContainer";
import Card from "../UI/Card";
import Input from "../UI/Input";

const AddHotel = () => {
  const [isValidHotelRooms, setIsValidHotelRooms] = useState(true);
  const [selectedHotelRooms, setSelectedHotelRooms] = useState([]);

  const [name, setName] = useState({
    value: location.state.name,
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [type, setType] = useState({
    value: location.state.type,
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [city, setCity] = useState({
    value: location.state.city,
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [address, setAddress] = useState({
    value: location.state.address,
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [distance, setDistance] = useState({
    value: location.state.distance,
    valid: false,
    touched: false,
    validators: [validNumber({ min: 0 }), validRequire]
  });
  const [title, setTitle] = useState({
    value: location.state.title,
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [description, setDescription] = useState({
    value: location.state.description,
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [price, setPrice] = useState({
    value: location.state.price,
    valid: false,
    touched: false,
    validators: [validNumber({ min: 0 }), validRequire]
  });
  const [images, setImages] = useState({
    value: location.state.images,
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [featured, setFeatured] = useState({value: location.state.featured});
  
  const [error, setError] = useState();
  const navigate = useNavigate();

  const { rooms } = useContext(APIContext);
  // console.log(rooms);

  const hotelRoomsSelectHandler = (e) => {
    const updatedOptions = [...e.target.options]
      .filter(option => option.selected)
      .map(x => x.value);
    // console.log('updatedOptions', updatedOptions);
    setSelectedHotelRooms(updatedOptions);
  }

  const addHotelHandler = (e) => {
    e.preventDefault();

    setTouched(setName);
    setTouched(setType);
    setTouched(setCity);
    setTouched(setAddress);
    setTouched(setDistance);
    setTouched(setTitle);
    setTouched(setDescription);
    setTouched(setPrice);
    setTouched(setImages);

    if(selectedHotelRooms.length === 0) {
      setIsValidHotelRooms(false);
    } else {
      setIsValidHotelRooms(true);
    };

    if(
      isValidHotelRooms === false ||
      !name.valid ||
      !type.valid ||
      !city.valid ||
      !address.valid ||
      !distance.valid ||
      !title.valid ||
      !description.valid ||
      !price.valid ||
      !images.valid
    ) {
      return;
    }

    const addHotel = {
      name: name,
      type: type,
      city: city.split(' ').map(i => i[0].toUpperCase() + i.substring(1)).join(' '),
      address: address,
      distance: distance,
      title: title,
      description: description,
      price: price,
      images: images.split('\n').join(','),
      featured: featured,
      rooms: selectedHotelRooms
    }

    axios
      .post('/hotel/add-hotel', addHotel)
      .then(res => console.log(res))
      .catch(err => console.log(err))

    navigate('/hotel');
  }

  return (
    <PageContainer>
      <Card className="ahCard">
        <div className="listTitle ahListTitle">add new hotel</div>
      </Card>
      <Card className="ahCardForm">
        <form className="formControl">
          <Input 
            id="name"
            isValid={isValidName}
            label="Name"
            type="text"
            placeholder="My Hotel"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />
          <Input 
            id="type"
            isValid={isValidType}
            label="Type"
            type="text"
            placeholder="hotel"
            value={type}
            onChange={(e) => setType(e.target.value)}
          />
          <Input 
            id="city"
            isValid={isValidCity}
            label="City"
            type="text"
            placeholder="New York"
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
          <Input 
            id="address"
            isValid={isValidAddress}
            label="Address"
            type="text"
            placeholder="eiton st. 216"
            value={address}
            onChange={(e) => setAddress(e.target.value)}
          />
          <Input 
            id="distance"
            isValid={isValidDistance}
            label="Distance form City Center"
            type="number"
            placeholder="500"
            value={distance}
            onChange={(e) => setDistance(e.target.value)}
          />
          <Input 
            id="title"
            isValid={isValidTitle}
            label="Title"
            type="text"
            placeholder="The best hotel"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
          />
          <Input 
            id="description"
            isValid={isValidDescription}
            label="Description"
            type="text"
            placeholder="description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
          />
          <Input 
            id="price"
            isValid={isValidPrice}
            label="Price"
            type="number"
            placeholder="100"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
          />
          <div className="inputFormControl">
            <div 
              className={"formLabel " + (isValidImages ? "" : "labelInvalid")}
              >Images
            </div>
            <textarea 
              name="ahImages" 
              className={"ahImages " + (isValidImages ? "" : "labelInvalid")} 
              value={images}
              onChange={(e) => setImages(e.target.value)}
              rows="3"
              cols="50"
            />
          </div>
          <div className="inputFormControl">
            <div 
              className={"formLabel "}
              >Featured
            </div>
            <select 
              name="ahFeatured" 
              id="ahFeatured"
              className={"ahFeatured "} 
              value={featured}
              onChange={(e) => setFeatured(e.target.value)}
            >
              <option value={false}>No</option>
              <option value={true}>Yes</option>
            </select>
          </div>
          <div className="inputFormControl ahHotelRooms">
            <div 
              className={"formLabel " + (isValidHotelRooms ? "" : "labelInvalid")}
              >Rooms (multiple select: ctrl + click)
            </div>
            <select 
              name="ahHotelRooms" 
              id="ahFeatured"
              className={"ahHotelRooms " + (isValidHotelRooms ? "" : "labelInvalid")} 
              value={selectedHotelRooms}
              onChange={hotelRoomsSelectHandler}
              multiple
            >
              {rooms.map(r => 
                <option value={r._id} key={r._id}>{r.desc}</option>
              )}
            </select>
          </div>
          <button 
            className="btnSend"
            onClick={addHotelHandler}
          >Send
          </button>
        </form>
      </Card>
    </PageContainer>
  );
};

export default AddHotel;
