import './DashBoard.css';
import { useEffect, useState } from 'react';
import { NavLink, useNavigate } from 'react-router-dom';
import ErrorModal from '../UI/ErrorModal';
import Card from '../UI/Card';
import OrderAPI from '../../API/OrderAPI';
import queryString from 'query-string';

const DashBoard = () => {
  const [error, setError] = useState();
  const [users, setUsers] = useState([]);
  const [orders, setOrders] = useState([]);
  const cookieId = document.cookie.split('=')[1];

  useEffect(() => {
    const params = {
      cookieId: cookieId
    }
    const query = '?' + queryString.stringify(params);
    OrderAPI.getAllOrders(query)
      .then(res => {
        console.log(res);
        setOrders(res);
      });
  })

  const viewHandler = e => {
    e.preventDefault();
  }

  const errorHandler = e => {
    e.preventDefault();
  }

  return (
    <div className="listContainer">
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={errorHandler}
          onCancel={errorHandler}
          isDelete={error.isDelete}
          showOkay={error.showOkay}
        />
      )}
      <Card className="listCard tstCard">
        <table>
          <thead>
            <tr>
              <th>
                <div>ID User</div>
              </th>
              <th>
                <div>Name</div>
              </th>
              <th>
                <div>Phone</div>
              </th>
              <th>
                <div>Address</div>
              </th>
              <th>
                <div>Total</div>
              </th>
              <th>
                <div>Delivery</div>
              </th>
              <th>
                <div>Status</div>
              </th>
              <th>
                <div>Detail</div>
              </th>
            </tr>
          </thead>
          <tbody>
            {orders && orders.map(i => 
              <tr key={i._id}>
                <td>{i.userId}</td>
                <td>{i.fullname}</td>
                <td>{i.phone}</td>
                <td>{i.address}</td>
                <td>{i.total}</td>
                <td>{i.delivery || 'ok'}</td>
                <td>{i.status || 'ok'}</td>
                <td>
                  <button 
                    id={i._id}
                    className="update action-btn"
                    onClick={viewHandler}
                  >View</button>
                </td>
              </tr>
            )}
          </tbody>
        </table>
      </Card>
    </div>
  );
};

export default DashBoard;
