import axios from "axios";
import { useContext, useEffect, useState } from 'react';
import './AddProduct.css';
import { useNavigate } from "react-router-dom";
import PageContainer from "../pageContainer/PageContainer";
import Card from "../UI/Card";
import Input from "../UI/Input";
import { onBlurHandler, onChangeHanlder, validLength, validNumber, validRequire } from "../../util/validators";
import { setTouched } from "../hook/lib";
import ErrorModal from "../UI/ErrorModal";

const AddProduct = () => {
  const [isValidHotelRooms, setIsValidHotelRooms] = useState(true);
  const [selectedHotelRooms, setSelectedHotelRooms] = useState([]);

  const [name, setName] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [type, setType] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [city, setCity] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [address, setAddress] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [distance, setDistance] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validNumber({ min: 0 }), validRequire]
  });
  const [title, setTitle] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [description, setDescription] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [price, setPrice] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validNumber({ min: 0 }), validRequire]
  });
  const [images, setImages] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [featured, setFeatured] = useState(false);
  
  const [error, setError] = useState();
  const navigate = useNavigate();

  const [rooms, setRooms] = useState([]);
  useEffect(() => {
    axios.get('/room')
      .then(res => setRooms(res.data))
      .catch(err => console.log(err))
  }, [])
  // console.log(rooms);

  const hotelRoomsSelectHandler = (e) => {
    const updatedOptions = [...e.target.options]
      .filter(option => option.selected)
      .map(x => x.value);
    // console.log('updatedOptions', updatedOptions);
    setSelectedHotelRooms(updatedOptions);
  }

  const addHotelHandler = (e) => {
    e.preventDefault();

    setTouched(setName);
    setTouched(setType);
    setTouched(setCity);
    setTouched(setAddress);
    setTouched(setDistance);
    setTouched(setTitle);
    setTouched(setDescription);
    setTouched(setPrice);
    setTouched(setImages);

    if(selectedHotelRooms.length === 0) {
      setIsValidHotelRooms(false);
    } else {
      setIsValidHotelRooms(true);
    };

    if(
      !isValidHotelRooms ||
      !name.valid ||
      !type.valid ||
      !city.valid ||
      !address.valid ||
      !distance.valid ||
      !title.valid ||
      !description.valid ||
      !price.valid ||
      !images.valid
    ) {
      return;
    }

    const addHotel = {
      name: name.value,
      type: type.value,
      city: city.value.split(' ').map(i => i[0].toUpperCase() + i.substring(1)).join(' '),
      address: address.value,
      distance: distance.value,
      title: title.value.split(' ').join(' ').charAt(0).toUpperCase() + title.value.split(' ').join(' ').slice(1),
      description: description.value.split(' ').join(' ').charAt(0).toUpperCase() + description.value.split(' ').join(' ').slice(1),
      price: price.value,
      // images: images.value.split('\n').join(','),
      images: images.value,
      featured: featured,
      rooms: selectedHotelRooms
    }

    console.log(addHotel);

    axios
      .post('/hotel/add-hotel', addHotel)
      .then(res => console.log(res))
      .catch(err => console.log(err))

    navigate('/hotel');
  }

  return (
    <>
      {error && (
        <ErrorModal
          title={error.title}
          message={error.message}
          onConfirm={() => setError(null)}
          error={!!error}
        />
      )}
      <PageContainer>
        <Card className="ahCard">
          <div className="listTitle ahListTitle">add hotel</div>
        </Card>
        <Card className="ahCardForm">
          <form className="formControl">
            <Input 
              control="input"
              id="name"
              type="text"
              placeholder="My Hotel"
              label="Name"
              required={true}
              onChange={e => onChangeHanlder(e, setName, name.validators)}
              onBlur={e => onBlurHandler(e, setName, name.validators)}
              value={name.value}
              isValid={name.valid}
              isTouched={name.touched}
            />
            <Input 
              control="input"
              id="type"
              type="text"
              placeholder="hotel"
              label="Type"
              required={true}
              onChange={e => onChangeHanlder(e, setType, type.validators)}
              onBlur={e => onBlurHandler(e, setType, type.validators)}
              value={type.value}
              isValid={type.valid}
              isTouched={type.touched}
            />
            <Input 
              control="input"
              id="city"
              type="text"
              placeholder="New York"
              label="City"
              required={true}
              onChange={e => onChangeHanlder(e, setCity, city.validators)}
              onBlur={e => onBlurHandler(e, setCity, city.validators)}
              value={city.value}
              isValid={city.valid}
              isTouched={city.touched}
            />
            <Input 
              control="input"
              id="address"
              type="text"
              placeholder="eiton st. 216"
              label="Address"
              required={true}
              onChange={e => onChangeHanlder(e, setAddress, address.validators)}
              onBlur={e => onBlurHandler(e, setAddress, address.validators)}
              value={address.value}
              isValid={address.valid}
              isTouched={address.touched}
            />
            <Input 
              control="input"
              id="distance"
              type="number"
              placeholder="500"
              label="Distance form City Center"
              required={true}
              onChange={e => onChangeHanlder(e, setDistance, distance.validators)}
              onBlur={e => onBlurHandler(e, setDistance, distance.validators)}
              value={distance.value}
              isValid={distance.valid}
              isTouched={distance.touched}
            />
            <Input 
              control="input"
              id="title"
              type="text"
              placeholder="The best hotel"
              label="Title"
              required={true}
              onChange={e => onChangeHanlder(e, setTitle, title.validators)}
              onBlur={e => onBlurHandler(e, setTitle, title.validators)}
              value={title.value}
              isValid={title.valid}
              isTouched={title.touched}
            />
            <Input 
              control="input"
              id="description"
              type="text"
              placeholder="description"
              label="Description"
              required={true}
              onChange={e => onChangeHanlder(e, setDescription, description.validators)}
              onBlur={e => onBlurHandler(e, setDescription, description.validators)}
              value={description.value}
              isValid={description.valid}
              isTouched={description.touched}
            />
            <Input 
              control="input"
              id="price"
              type="number"
              placeholder="100"
              label="Price"
              required={true}
              onChange={e => onChangeHanlder(e, setPrice, price.validators)}
              onBlur={e => onBlurHandler(e, setPrice, price.validators)}
              value={price.value}
              isValid={price.valid}
              isTouched={price.touched}
            />
            <Input 
              control="textarea"
              id="images"
              type="textarea"
              placeholder=""
              label="Images"
              required={true}
              onChange={e => onChangeHanlder(e, setImages, images.validators)}
              onBlur={e => onBlurHandler(e, setImages, images.validators)}
              value={images.value}
              isValid={images.valid}
              isTouched={images.touched}
              rows="3"
              cols="50"
            />
            <button 
              className="btnSend"
              onClick={addHotelHandler}
            >Submit
            </button>
          </form>
        </Card>
      </PageContainer>
    </>
  );
};

export default AddProduct;
