import { useEffect, useState } from "react";
import './auth.css'
import { useDispatch } from "react-redux";
import { useNavigate } from "react-router-dom";
import { authActions } from "../../hook/auth";
import { currentUser, saveToStorage } from "../../hook/lib";
import { useAPI } from "../../store/APIContext";
import axios from "../../util/axios";

const Login = (pros) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const [errorUsername, setErrorUsername] = useState('');
  const [errorPassword, setErrorPassword] = useState('');

  const [isValidUsername, setIsValidUsername] = useState(true);
  const [isValidPassword, setIsValidPassword] = useState(true);

  const { users } = useAPI();

  console.log(users);
 
  const dispatch = useDispatch();

  const navigate = useNavigate();

  const onChangeUsername = (e) => {
    setUsername(e.target.value);
  };

  const onChangePassword = (e) => {
    setPassword(e.target.value);
  };

  const loginHandler = (e) => {
    e.preventDefault();

    // console.log(username);

    if(!username) {
      setIsValidUsername(false);
      setErrorUsername('username is empty');
    } else {
      setIsValidUsername(true);
    }

    if(password.trim() === '') {
      setIsValidPassword(false);
      setErrorPassword('password is empty');
    } else {
      setErrorUsername(true);
    }
    const isUserAdmin = users.filter(i => i.username.includes(username))[0].isAdmin;
    const filteredUser = users.filter(i => i.username.includes(username))[0];
    
    if(!users.map(i => i.username).includes(username)) {
      setIsValidUsername(false);
      setErrorUsername(username + ' does not exist');
    } else if(!isUserAdmin) {
      setIsValidUsername(false);
      setErrorUsername(username + ' is not admin');
    } else {
      setIsValidUsername(true);
    }
    
    console.log(users);
    console.log('u '+ isValidUsername);
    console.log(isUserAdmin)
    console.log('p '+ isValidPassword);
    
    if(!isValidUsername || !isValidPassword) {
      return;
    }

    if(isValidUsername && isValidPassword) {
      if(
        isUserAdmin &&
        filteredUser.password === password 
      ) {
        dispatch(authActions.login());
        while(currentUser.length > 0) {
          currentUser.pop();
        };
        currentUser.push(filteredUser);
        saveToStorage('currentUser', currentUser);
        // console.log(currentUser);
        // navigate('/home');
      } else {
        setIsValidUsername(false);
        return;
      }
    }
  };
  return <>
    <main className="centered">
      <h1>Login</h1>
      <form className="form-group">
        <input 
          type="text" 
          placeholder="username" 
          className="form-control" 
          value={username}
          onChange={onChangeUsername}
        />
        {!isValidUsername && (<span style={{ color: "red", marginBottom: "1rem" }}>{errorUsername}</span>)}
        <input 
          type="password" 
          placeholder="password" 
          className="form-control" 
          value={password}
          onChange={onChangePassword}
        />
        {!isValidPassword && (<span style={{ color: "red", marginBottom: "1rem" }}>{errorPassword}</span>)}
        <button 
          type="submit" 
          className="btn"
          onClick={loginHandler}
        >Login</button>
      </form>
    </main>
  </>
}

export default Login;
