import axios from "../../util/axios";
import { useContext, useState } from "react";
import './auth.css'
import { useDispatch } from "react-redux";
import { authActions } from "../hook/auth";
import { currentUser, saveToStorage, setTouched } from "../hook/lib";
import { useNavigate } from "react-router-dom";
import Input from "../UI/Input";
import ErrorModal from "../UI/ErrorModal";
import { onBlurHandler, onChangeHanlder, validLength, validRequire } from "../../util/validators";
import PageContainer from "../pageContainer/PageContainer";
import queryString from "query-string";
import UserAPI from "../../API/UserAPI";

const SignUp = (pros) => {
  const [fullname, setFullname] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [email, setEmail] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [password, setPassword] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [phone, setPhone] = useState({
    value: '',
    valid: false,
    touched: false,
    validators: [validLength({ min: 4 }), validRequire]
  });
  const [error, setError] = useState();

  // const { users } = useAPI();
  const dispatch = useDispatch();
  const navigate = useNavigate();


  const signUpHandler = (e) => {
    e.preventDefault();

    setTouched(setFullname);
    setTouched(setEmail);
    setTouched(setPassword);
    setTouched(setPhone);

    if(!email.valid || !password.valid) {
      return setError({
        title: 'Validation failed!',
        message: 'Invalid email or password!'
      })
    }

    if(email.valid && password.valid) {
      const params = {
        email: email.value,
        password: password.value,
        fullname: fullname.value,
        phone: phone.value
      };
      const query = '?' + queryString.stringify(params);

      UserAPI.postSignUp(query)
        .then(res => {
          if(res.status === 200 || res.status === 201) {
            // console.log(res);
            dispatch(authActions.login());
            while(currentUser.length > 0) {
              currentUser.pop();
            };
            currentUser.push(res.data);
            saveToStorage('currentUser', currentUser);
            navigate('/home');
          }
        })
        .catch(err => {
          if(err.response.status) {
            setError({
              title: 'Validation failed!',
              message: err.response.data.message
            })
          }
        });
    }
  };

  return (
    <>
      {error && (
        <ErrorModal 
          title={error.title}
          message={error.message}
          onConfirm={() => setError(null)}
          error={!!error}
        />
      )}
      <PageContainer>
        <main className="centered">
          <h1>SignUp</h1>
          <form className="form-group">
            <Input
              control="input"
              id="username"
              type="text" 
              placeholder="username" 
              required={true}
              onChange={e => onChangeHanlder(e, setUsername, username.validators)}
              onBlur={e => onBlurHandler(e, setUsername, username.validators)}
              value={username.value}
              isValid={username.valid}
              isTouched={username.touched}
            />
            <Input 
              control="input"
              id="password"
              type="password" 
              placeholder="password"
              onChange={e => onChangeHanlder(e, setPassword, password.validators)}
              onBlur={e => onBlurHandler(e, setPassword, password.validators)}
              required={true}
              value={password.value}
              isValid={password.valid}
              isTouched={password.touched}
            />
            <button 
              type="submit" 
              className="btn"
              onClick={signUpHandler}
            >SignUp</button>
          </form>
        </main>
      </PageContainer>
    </>
  );
};

export default SignUp;
